css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("wifi-iot.com") {
body, .theme,.navbar-default,.navbar-fixed-top {
	/*background-image: url("http://www.kartinki.me/pic/201209/1280x768/kartinki.me-8020.jpg") !important;*/
	background-image: url("http://wallpapercraft.net/wp-content/uploads/2016/10/Dark-Gray-Backgrounds.jpg") !important;
	background-color: #000000 !important;
	background-attachment: fixed !important;
	background-clip: border-box !important;
	background-origin: padding-box !important;
	background-position: center center !important;
	background-repeat: no-repeat !important;
	background-size: cover !important;
    color:#95acdd;
	}
	
	td, .theme,.navbar-default,.navbar-fixed-top {
	/*background-image: url("http://365psd.ru/images/backgrounds/black_paper.png") !important;
	background-image: url("http://365psd.ru/images/backgrounds/dark-texture-2.png") !important;*/
	background-color: #222 !important;
	background-attachment: fixed !important;
	background-clip: border-box !important;
	background-origin: padding-box !important;
	background-position: center center !important;
	background-repeat: no-repeat !important;
	background-size: cover !important;
	}
a {
 color:#776;
 text-decoration:none
}
h3,h2,h1,h4,li {
 font-weight:normal;
 line-height:1;
 color:#99a
}
br,p,label,td,th {
 font-weight:normal;
 line-height:1;
 color:#778
}
.modal-content,.muted,.active {
 position:relative;
	background-image: url("http://wallpapercraft.net/wp-content/uploads/2016/10/Dark-Gray-Backgrounds.jpg") !important;
 background-color:#aaa;
 border:1px solid #eee;
 border:1px solid rgba(0,0,0,0.05);
 color:#aaa;
 border-radius:5px;
 -webkit-box-shadow:0 3px 9px rgba(0,0,0,0.5);
 box-shadow:0 3px 9px rgba(0,0,0,0.5);
 -webkit-background-clip:padding-box;
 background-clip:padding-box;
 outline:0
}
.form-control {
 display:block;
 width:100%;
 height:38px;
 padding:7px 12px;
 font-size:14px;
 line-height:1.42857143;
 color:#222;
 background-color:#777;
 background-image:none;
 border:1px solid #431;
 border-radius:4px;
 -webkit-box-shadow:inset 0 1px 1px rgba(0,0,0,0.075);
 box-shadow:inset 0 1px 1px rgba(0,0,0,0.075);
 -webkit-transition:border-color ease-in-out .15s,-webkit-box-shadow ease-in-out .15s;
 -o-transition:border-color ease-in-out .15s,box-shadow ease-in-out .15s;
 transition:border-color ease-in-out .15s,box-shadow ease-in-out .15s
}
.form-control:focus {
 border-color:#aaa;
 outline:0;
 -webkit-box-shadow:inset 0 1px 1px rgba(0,0,0,.075),0 0 8px rgba(200,200,200,0.6);
 box-shadow:inset 0 1px 1px rgba(0,0,0,.075),0 0 8px rgba(200,200,200,0.6)
}
}
