css:
@-moz-document domain(pivotaltracker.com) {
  .edit .task, .ghost .task, .edit .task_new, .ghost .task_new {
    background-color: #444;
    border: 1px solid #aaa;
  }
  
  .model section.edit li.activity, .model section.edit .mini.attachments .attachments {
    background-color: #333;
    border: 1px solid #222;
  }
  
  a.button.tool, button.tool, input.tool[type="submit"] {
    background-color: #333;
  }
  
  .iteration .preview {
    background-color: #222222;
    border-color: #111 !important;
  }
  
  .story.feature .preview .owner, .story.bug .preview .owner, .story.chore .preview .owner {
    color: #99d;
  }
  
  section.edit .tracker_markup .story.flyoverable {
    background-color: #222 !important;
    border-color: #111 !important;
    color: #ccc;
  }
  
  .tracker_markup code {
    background-color: #335;
    color: #ccc;
    border-color: #111;
  }
    
  .dropdown_menu {
    background-color: #333 !important;
    color: #ccc !important;
  }
  
  .dropdown_menu .search_item {
    border-color: #222 !important;
    background-color: #333 !important;
  }
  
  .dropdown_menu .dropdown_label {
    color: #ccc !important;
  }
  
  .dropdown_menu .dropdown_item.selected a,
  .dropdown_menu .dropdown_item.hover a {
    background: #444 !important;
  }
  
  .end_of_list.item {
    background-color: #222 !important;
    background-image: url(data:image/png;base64,) !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-attachment: scroll !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
  }
  
  .panel .panel_content {
    background-color: #222 !important;
    background-image: url(data:image/png;base64,) !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-attachment: scroll !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
  }
  
  .panel.epic_history .activity_entry .preview, 
  .panel.project_history .activity_entry .preview, 
  .panel.story_history .activity_entry .preview {
    background-color: #333;
  }
  
  .mention {
    background: #222 !important;
    border-color: #111 !important;
  }
  
  .story .preview {
    background: rgb(74, 74, 74) !important;
    border-top: 1px solid #363636 !important;
    border-bottom: 1px solid #363636 !important;
  }
  
  .story.accepted .preview,
  .story.unscheduled .preview {
    background: rgba(9, 100, 164, 0.81) !important;
  }
  
  .story.delivered .preview, 
  .story.finished .preview, 
  .story.rejected .preview, 
  .story.started .preview {
    background: rgba(106, 152, 114, 0.77) !important;
  }
    
  .tracker_markup .labels {
    -moz-filter: brightness(0.8) contrast(1) invert(1) sepia(0);
    -webkit-filter: brightness(0.8) contrast(1) invert(1) sepia(0);
    filter: brightness(0.8) contrast(1) invert(1) sepia(0);
  }
  
  .labels_maker {
    background: #333 !important;
    border-color: #333 !important;
  }
  
  .labels_maker .selected.labels {
    /* What an odd bug. */
    z-index: 1000;
  }
  
  .story .preview .story_name {
    color: #CCC;
  }
  
  .preview .selector {
    -moz-filter: brightness(0.8) contrast(1) invert(1) sepia(0);
    -webkit-filter: brightness(0.8) contrast(1) invert(1) sepia(0);
    filter: brightness(0.8) contrast(1) invert(1) sepia(0);
  }
  
  .story.release .preview {
    background: #2b4c74 !important;
    border-top: 1px solid #2b4c75 !important;
    border-bottom: 1px solid #2b4c75 !important;
  }
  
  .story.item section.edit {
    background: #333 !important;
  }
  
  .new section.model_details .persistence {
    background: #444;
  }
  
  .story.item .edit textarea {
    background-color: #484848;
    color: #ddd;
  }
  
  .story.item .edit h4,
  .story.item .edit a.selection
  {
    color: #eee;
  }
  
  .story.item .edit .description .field_controls {
    background-color: #444;
  }
  
  .story.item .edit .cancel {
    color: #ccc;
  }
  
  .story.item .edit .edit_description {
    color: #dde;
  }
  
  .story.item .edit .rendered_description {
    color: #ccc;
    background-color: #444 !important;
  }
  
  .story.item .edit .maximize {
    background-color: #444;
  }
  
  .story.item .edit .maximize:hover {
    background-color: #595959;
    border-color: #ccc;
    color: #ccc;
  }
  
  .story.item .edit .labels input {
    background: #444;
  }
  
  .story.item .edit .labels .dropdown,
  .story.item .edit .labels .dropdown_menu {
    background-color: #444;
  }
  
  .story_flyover {
    background: #333 !important;
  }
  
  .story_flyover .details {
    background: #222 !important;
    box-shadow: none !important;
    border-bottom: 1px solid #111 !important;
  }
  
  .story_flyover footer {
    background: #222 !important;
  }
  
  .flyover.epic_flyover section.activity ol > li:nth-child(2n), .flyover.followers_flyover section.activity ol > li:nth-child(2n), .flyover.integration_story_flyover section.activity ol > li:nth-child(2n), .flyover.story_flyover section.activity ol > li:nth-child(2n) {
    background: #444 !important;
  }
  
  .model section.activity ol.comments .activity.github::before {
    -moz-filter: invert(1);
    -webkit-filter: invert(1);
    filter: invert(1);
  }
  
  .model section.edit li.activity.github, .model section.edit .mini.attachments .attachments.github {
    border-color: #888;
  }
  
  .model section.activity ol.comments .activity.github .author {
    color: #eee;
  }
  
  .model section.activity ol.comments .activity.github .url a:link, .model section.activity ol.comments .activity.github .url a:hover, .model section.activity ol.comments .activity.github .url a:visited {
    color: #bbf;
  }
  
  .model section.activity .activity .message {
    color: #ccc;
  }
  
  .panel.epic_history .activity_entry .details, 
  .panel.project_history .activity_entry .details, 
  .panel.story_history .activity_entry .details {
    background-color: #222;
    color: #ccc;
  }
  
  .lightbox .title {
    background: #222 !important;
    color: #ccc !important;
  }
  
  .lightbox .content {
    background: #222 !important;
    color: #ccc !important;
  }
  
  .lightbox .content li:hover {
    background: #333 !important;
    color: #ccc !important;
  }
  
  .lightbox .content li.selected {
    background: #444 !important;
    color: #ccc !important;
  }
  
  section.model_details .actions button {
    -moz-filter: brightness(0.8) invert(1);
    -webkit-filter: brightness(0.8) invert(1);
    filter: brightness(0.8) invert(1);
  }
  
  .button.restart {
    -moz-filter: brightness(0.8) contrast(1) invert(1) sepia(0);
    -webkit-filter: brightness(0.8) contrast(1) invert(1) sepia(0);
    filter: brightness(0.8) contrast(1) invert(1) sepia(0);
  }
  
  .button.reveal {
    background-color: #444 !important;
    border-color: #111 !important;
  }
  
  .button.reveal:hover {
    background-color: #555 !important;
  }
  
  section.model_details .actions button:hover {
    -moz-filter: none;
    -webkit-filter: none;
    filter: none;
    border-color: #ccc !important;
  }
  
  section.model_details .button_with_field .text_value {
    background-color: #444;
    color: #ccc;
  }
  
  .info_box {
    background-color: #444 !important;;
  }
  
  .info_box em {
    color: #aaa !important;
  }
  
  .info_box .row, .info_box .row.focused {
    background-color: #444 !important;
  }
  
  .initials {
    background-color: #333 !important;
    color: #bbb !important;
    border-color: #222 !important;
  } 
  
  .initials:hover {
    color: #fff !important;
    background-color: #555 !important;
    border-color: #222 !important;
  }

  .name {
    color: #eee !important;
  }
  
  .add_owner {
    border-color: #999 !important;
     -moz-filter: brightness(0.8) invert(1);
    -webkit-filter: brightness(0.8) invert(1);
    filter: brightness(0.8) invert(1);
  }
  
  .info_box .row.timestamp_wrapper {
    background-color: #444;
  }
  
  .timestamp .timestamp_row {
    color: #ccc;
    text-shadow: none;
  }
  
  .message.tracker_markup {
    color: #ccc;
  }
  
  .tracker_markup {
    color: #444;
  }
  
  .tracker_markup a {
    color: #aae;
  }
  
  .unscheduled .state {
    color: #777 !important;
  }
  
  .state.button.start {
    color: #eee !important;
    background-color: #555 !important;
    border-color: #202020;
  }

  .state.button {
  	margin-top: 2px;                 
  }

  .preview .button.state, .preview .estimate, .preview .reveal.button {
  	margin-top: 1px; 	                     
  }
  
  .epic_stories_header {
    background-color: #633;
    border-color: #111 !important;
  }
  
  .epic_stories_header .reveal {
    color: #ccc !important;
    text-shadow: 0 1px 1px #111 !important;
  }
  
  .activity_entry_group, .epics .done_bar.reveal, .done_bar, .epics .done_bar.hide {
    border-color: #111 !important;
  }
  
  .activity_entry_group a, .epics .done_bar.reveal a, .done_bar a, .epics .done_bar.hide a, .activity_entry_group label, .epics .done_bar.reveal label, .done_bar label, .epics .done_bar.hide label {
    background: #336 !important;
    color: #ccc !important;
  }
  
  .epic_stories_header .counts {
    color: #aaa !important;
  }
  
  input.std {
    background: #444 !important;
    color: #ccc !important;
  }
}
