css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.gkh-kemerovo.ru") 
{

  * {
    margin: 0; 
    padding: 0;
  }
  
  body { 
    background: url(http://i.imgur.com/qzxhSZh.jpg);
    background-size: 1920px 1080px;
    background-attachment: fixed;
  }
  
  #mnu1, #mnu2, #mnu3, #mnu4, #mnu5, #mnu6, #mnu7 {
    background-color: #3d84c5;
    margin: 15px 15px;
    width: 344px;
    height: 90px;
    border-radius: 2px;
  }
  
  #mnu1 {
    background: url('http://i.imgur.com/VacH485.png') no-repeat;
  }
  
  #mnu2 {
    background: url('http://i.imgur.com/F9L1jp7.png') no-repeat;
  }
  
  #mnu3 {
    background: url('http://i.imgur.com/TwTFkQw.png') no-repeat;
  }
  
  #mnu4 {
    background: url('http://i.imgur.com/3QTaUh7.png') no-repeat;
  }
  
  #mnu5 {
    background: url('http://i.imgur.com/5c8RNQH.png') no-repeat;
  }
  
  #mnu6 {
    background: url('http://i.imgur.com/PIHm3L9.png') no-repeat;
  }
  
  #mnu7 {
    background: url('http://i.imgur.com/2IByB0V.png') no-repeat;
  }
  
  big > b {
    font-family: Roboto !important;
    text-transform: uppercase;
    background-color: #0091d8;
    border-radius: 2px;
    padding-bottom: 60px;
    padding-top: 10px;
    padding-left: 142px;
    padding-right: 142px;
    
  }
  
  #mni > div {
    padding-top: 15px;
    color: #ff0033 !important;
    font-family: Roboto !important;
    text-transform: uppercase;
    text-shadow: 1px 1px 1px #383838;
  }
  
  [href*="page=1029"] {
    background-color: #0091d8;
    padding-bottom: 10px;
    padding-top: 10px;
    padding-left: 142px;
    padding-right: 142px;
    font-family: Roboto !important;
    text-transform: uppercase;
    color: white;
    text-decoration: none;
    border: 0px solid;
    border-radius: 2px;
  }
  
  [href*="page=1029"]:hover {
    background-color: #40b9f4;
  }
  
  #ric_anchor {
    background: url(http://i.imgur.com/4HZzIiX.png);
    background-position: 650px 150px;
    background-repeat: no-repeat;
    padding: 90px 650px;
    margin: -110px 0px 20px;
  }
  
  table.lbox {
    border: 0px solid #39afd6;
    background-color: white;
    border-radius: 2px;
    box-shadow: none;
    padding-top: 40px;
    padding-bottom: 0;
    padding-left: 40px;
    padding-right: 40px;
  }
  
  #enter {
    background: url('http://i.imgur.com/EpRc7Bo.png') no-repeat;
    background-image: url('http://i.imgur.com/EpRc7Bo.png');
    width: 144px;
    height: 40px;
    border-radius: 2px;
  }
  
  #enter:hover {
    background: url('http://i.imgur.com/6jjwcEn.png') no-repeat;
    background-image: url('http://i.imgur.com/6jjwcEn.png');
    width: 144px;
    height: 40px;
  }
  
  div.p2 {
    background: #83b8ff repeat;
    font-size: 1.255em !important;
    font-family: Roboto, Sans-serif;
  }
  
  div.p1 {
    font-family: Roboto, Sans-serif;
    font-size: 1.255em !important;
  }
  
  #logintype {
    color: #105576;
    background: url(http://i.imgur.com/izkAfoi.png);
    background-repeat: no-repeat;
    background-size: 15px;
    background-position-x: 370px;
    background-position-y: 9px;
    border: 2px solid #6ec3dc;
    border-top-width: 2px;
    border-right-width: 2px;
    border-bottom-width: 2px;
    border-left-width: 2px;
    border-color: rgb(36, 81, 115);
    border-radius: 3px;
    font-family: Roboto;
    text-transform: uppercase;
    width: 400px;
    height: 34px;
  }
  
  td.lctr {
    background-size: 300px;
    padding-top: 10px;
    padding-bottom: 40px;
    padding-left: 50px;
    padding-right: 50px;
    border: 0px;
    border-bottom: 0px solid #105576;
    font-family: Roboto;
    text-transform: uppercase;
  }
  
  #data1 {
    width: 400px;
  }
  
  #an {
    width: 200px;
    font-family: Roboto;
    text-transform: uppercase;
    padding: 12px;
  } 
  
  .lgn, .lgns, .lgnp, .lgnsp {
    width: 250px;
    height: 27px;
  }
  
  #el {
    font-family: Roboto;
    text-transform: uppercase;
    padding-right: 90px;
    padding-top: 13px;
  }
  
  .lgn {
    font-family: Roboto;
    font-size: 18px;
  }
  
  #visa {
    background: url("http://i.imgur.com/C5PZ7w8.png");
    background-repeat: no-repeat;
    background-size: 90px 40px;
    background-position-x: 20px;
    background-position-y: 20px;
    width: 120px;
    height: 66px;
  }
  
  #visal {
    background: url("http://i.imgur.com/GFRIp9T.png");
    background-repeat: no-repeat;
    background-size: 90px 30px;
    background-position-x: 20px;
    background-position-y: 20px;
    width: 120px;
    height: 66px;
  }
  
  #mscd {
    background: url("http://i.imgur.com/VmxCkOe.png");
    background-repeat: no-repeat;
    background-size: 100px 40px;
    background-position-x: 20px;
    background-position-y: 20px;
    width: 120px;
    height: 66px;
  }
  
  #mscdl {
    background: url("http://i.imgur.com/jAC9Bh1.png");
    background-repeat: no-repeat;
    background-size: 70px 43px;
    background-position-x: 20px;
    background-position-y: 20px;
    width: 120px;
    height: 66px;
  }

  .tac, .cbt, .cbl, .cbr, .cbb, .cbrd, .cbld {
    color: black;
    background-color: white;
  }
  
  div > b > big {
    background-color: #0091d8;
    padding-bottom: 10px;
    padding-top: 10px;
    padding-left: 225px;
    padding-right: 225px;
    font-family: Roboto !important;
    text-transform: uppercase;
    color: white;
    text-decoration: none;
    border: 0px solid;
    border-radius: 2px;
  }
  
  #mtb {
    background-color: white;
    border: 2px solid white;
    border-radius: 2px;
  }
  
  small {
    color: white;
    font-family: Roboto;
    text-transform: uppercase;
    text-shadow: 2px 2px 2px black;
  }
  
  #ret {
    background: url('http://i.imgur.com/LN3EY04.png') no-repeat;
    width: 245px;
    height: 65px;
    border-radius: 2px;
  }
  
  #bbs {
    background: url('http://i.imgur.com/HDSTD1m.png') no-repeat;
    width: 245px;
    height: 65px;
    border-radius: 2px;
  }
  
  .ac3 {
    background: url('http://i.imgur.com/x47j46x.png') no-repeat;
    border-radius: 2px;
  }
  
  .ac1 {
    background: url('http://i.imgur.com/i9gXXNj.png') no-repeat;
    border-radius: 2px;
  }
  
  .hdr {
    font-family: Roboto;
    text-align: center;
    text-transform: uppercase;
    background: #0091d8;
    width: 200px;
    height: 40px;
    color: white;
    border-radius: 2px;
  }
  
  #numbr1, #numbr2, #numbr3  {
    margin-top: 20px;
    width: 50px;
    height: 50px;
    -webkit-border-radius: 50px;
    border-radius: 50px;
  }
  
  #numbr1 {
    background: url(https://i.imgur.com/iDn6YLj.png) !important;
  }
  
  #numbr2 {
    background: url(https://i.imgur.com/PLq4E04.png) !important;
  }
  
  #numbr3 {
    background: url(https://i.imgur.com/j0AYP7V.png) !important;
  }
  
  #numbr_btw1, #numbr_btw2 {
    margin-top: 20px;
    width: 140px;
  }
  
  #step_txt {
    text-align: center;
    font-family: Arial, Sans-serif;
    text-transform: uppercase;
    font-size: 18px;
  }
  
}
