css:
@-moz-document domain("www.loginportal.ch") {
html {
    background: #111111;
}
/* Scrollbar Loginportal*/

*::-webkit-scrollbar {
    width: 15px !important;
    height: 15px !important;
    background: #1e1e1e !important;
    border: 1px solid #252525 !important;
}
*::-webkit-scrollbar-button {
    display: none !important;
}
*::-webkit-scrollbar-thumb,
*::-webkit-scrollbar-track {
    border: 0 !important;
    box-shadow: none !important;
}
*::-webkit-scrollbar-thumb {
    min-height: 28px !important;
    background: #333333 !important;
}
*::-webkit-scrollbar-corner,
*::-webkit-scrollbar-track {
    background: #222222 !important;
}
/*------Informationen und Personen------*/
/*------main_user------*/

#main_user {
    width: auto;
    height: auto;
    color: #444444;
    font-size: 15px;
}
div.jaz_in {
    background-color: #71905a;
    border: 1px solid #006000;
}
div.jaz_out {
    background-color: #905656;
    border: 1px solid #702c2c;
}
/*------Logo------*/

#main_logo {
    -webkit-filter: brightness(0.85);
    filter: brightness(0.85);
}
/*------main_content------*/

#main_content {
    background: #222222 !important;
    color: #eeeeee !important;
}
p.warning,
table.warning {
    background: #ffcab7 !important;
    color: #111111 !important;
    font-size: 15px !important;
}
p a {
    color: #222222 !important;
}
h1 {
    color: #eeeeee !important;
    font-size: 24px !important;
}
h2 {
    color: #eeeeee !important;
    font-size: 22px !important;
}
h3 {
    color: #eeeeee !important;
    font-size: 20px !important;
}
td b {
    color: #eeeeee !important;
    font-size: 15px !important;
}
tr td {
    color: #eeeeee;
    font-size: 15px !important;
}
a {
    color: #444444 !important;
    font-size: 15px !important;
}
b {
    color: #222222 !important;
    font-size: 15px !important;
}
p {
    color: #cccccc !important;
    font-size: 15px !important;
}
p a {
    color: #555555 !important;
    font-size: 15px !important;
}
span a {
    background: #6db637;
}
/*------main_nav------*/

#main_nav div {
    border: 1px solid #888888 !important;
}
#main_nav div.active {
    background: #dbc42e !important;
}
#main_nav div.inactive {
    background: #777777 !important;
}
.active b a,
.inactive b a {
    color: #222222 !important;
    font-size: 20px !important;
}
li a {
    color: #555555 !important;
    font-size: 15px !important;
}
li b {
    color: #555555 !important;
    font-size: 15px !important;
}
b a {
    color: #555555 !important;
    font-size: 15px !important;
}
/*------Mein Profil------*/

div.polaroid {
    background: #333333 !important;
}
/*------Arbeitszeit------*/

tr.row_highlight {
    background: #333333 !important;
}
#main_content th {
    background: #444444 !important;
}
/*------input, select, textarea------*/

input {
    background: #777777 !important;
    border: #888888 1px solid;
    color: #222222 !important;
    font-size: 15px !important;
}
select {
    background: #777777 !important;
    border: #888888 1px solid;
    color: #222222 !important;
    font-size: 15px !important;
}
textarea {
    background: #777777 !important;
    border: #888888 1px solid;
    color: #222222 !important;
    font-size: 15px !important;
}
/*------notification------*/

div.notification {
    background-color: #71905a;
}
}
