css:
@-moz-document url-prefix("https://www.zhihu.com") {

/* Author: Yifeng Huang */
/* 问题页 */
/*===========================================*/
/*================== 宽屏适配 ==================*/
/*===========================================*/
.zm-meta-panel {
  padding: 7px 0 13px 0; }

@media (max-width: 1400px) and (min-width: 968px) {
  .zm-meta-panel {
    padding: 3px 0 !important; } }

/**
 *
 * 通用
 *
* -------------------------------------------------------------- */
body {
  background: #fff;
  color: #444;
  font-family: '.SFNSText-Regular', 'Helvetica Neue', Helvetica, Arial, "Hiragino Sans GB", "Microsoft YaHei", "WenQuanYi Micro Hei", sans-serif;
  letter-spacing: .02em; }

a {
  color: #175199; }
  a:hover {
    color: #0d79d1; }

.Card {
  border: none;
  border-radius: 0;
  box-shadow: none; }

.List-item {
  padding: 5px 10px;
  border: none; }

.RichContent {
  font-size: 14px;
  line-height: 24px; }

.QuestionHeader-edit {
  font-size: 12px;
  font-weight: normal; }

/**
 * Header
* -------------------------------------------------------------- */
header.Sticky.AppHeader {
  background: #0f88eb; }

header .AppHeader-inner {
  height: 40px; }
  header .AppHeader-inner .Icon.Icon--logo {
    fill: #fff !important; }
  header .AppHeader-inner .AppHeader-TabsLink {
    padding: 8px 0;
    color: #eee; }
    header .AppHeader-inner .AppHeader-TabsLink:hover {
      color: #fff; }
    header .AppHeader-inner .AppHeader-TabsLink.is-active {
      color: #fff; }
      header .AppHeader-inner .AppHeader-TabsLink.is-active:after {
        background: #fff; }
  header .AppHeader-inner .AppHeader-userInfo {
    top: 20px; }
    header .AppHeader-inner .AppHeader-userInfo .Button--plain {
      color: #E6E4E7; }
      header .AppHeader-inner .AppHeader-userInfo .Button--plain:hover {
        color: #fff; }
  header .AppHeader-inner .Button--blue:hover {
    background: #0077e6; }
  header .AppHeader-inner .Button--blue.AppHeader-login {
    color: #fff;
    border: 1px solid #fff; }
  header .AppHeader-inner .Button--blue.Button--primary {
    color: #fff;
    background: #0d7ad3; }
    header .AppHeader-inner .Button--blue.Button--primary:hover {
      border: 1px solid #fff; }

header .PushNotifications-count {
  padding: 0 5px;
  line-height: 1.5em;
  border: 0; }

.PageHeader.is-shown .TopstoryPageHeader {
  height: 40px; }

.PageHeader.is-shown .TopstoryPageHeader-main a {
  color: #E6E4E7; }

.PageHeader.is-shown .TopstoryPageHeader-main .ZhihuLogo {
  fill: #fff !important; }

.PageHeader.is-shown .TopstoryPageHeader-main .TopstoryPageHeader-tabsLink:hover {
  color: #fff; }

.PageHeader.is-shown .TopstoryPageHeader-main .TopstoryPageHeader-tabsLink.is-active {
  font-weight: bold; }

.PageHeader.is-shown .QuestionHeader-title {
  color: #fff;
  font-size: 16px; }

.PageHeader.is-shown .Button {
  color: #fff; }

.PageHeader.is-shown .QuestionHeader-side .Button--primary.Button--blue {
  background: #0c6cbb;
  border: 1px solid #3ba0f2; }
  .PageHeader.is-shown .QuestionHeader-side .Button--primary.Button--blue:hover {
    background: #0a5ea3;
    border: 1px solid #3ba0f2; }

.PageHeader.is-shown .QuestionHeader-side .Button--primary.Button--grey {
  background: #52b657;
  border: 1px solid #377d2d; }
  .PageHeader.is-shown .QuestionHeader-side .Button--primary.Button--grey:hover {
    background: #c5592f;
    border: 1px solid #b9a9c5; }

.PageHeader.is-shown .Icon.Button-icon.Icon--modify {
  fill: #fff; }

/**
 * Main App
* -------------------------------------------------------------- */
.App-main .QuestionHeader-title {
  font-size: 18px !important;
  font-weight: bold;
  color: #4d4c4c; }

.App-main .QuestionHeader {
  box-shadow: none;
  border-bottom: none;
  padding: 10px 0 0; }
  .App-main .QuestionHeader figure {
    width: 60%; }

.App-main .QuestionHeader-content .QuestionHeader-main {
  padding: 0 10px; }
  .App-main .QuestionHeader-content .QuestionHeader-main .QuestionHeader-topics .Tag {
    font-size: 12px;
    padding: 0 10px;
    line-height: 20px;
    height: 20px; }
  .App-main .QuestionHeader-content .QuestionHeader-main .QuestionHeader-detail .QuestionRichText {
    font-size: 14px;
    line-height: 23px; }

.App-main .QuestionHeader-footer .QuestionHeader-main {
  padding: 0 10px; }

.App-main .Question-main .Question-mainColumn .QuestionInvitation [data-za-module="UserList"] .List-item {
  padding: 0; }

.App-main .Question-main .Question-mainColumn .QuestionInvitation [data-za-module="UserList"] .UserItem-name {
  font-size: 10px !important; }

.App-main .Question-main .Question-mainColumn .QuestionInvitation [data-za-module="UserList"] .ContentItem-meta {
  font-size: 10px !important; }

.App-main .Question-main .Question-mainColumn .QuestionInvitation [data-za-module="UserList"] .PortraitItem-actions button {
  line-height: 24px;
  padding: 0 8px;
  font-size: 10px !important; }

.App-main .Question-main .Question-mainColumn .QuestionInvitation [data-za-module="UserList"] .PortraitItem {
  font-size: 10px !important; }

.App-main .Question-main .Question-mainColumn .QuestionInvitation [data-za-module="UserList"] .UserItem-avatar .Avatar.Avatar--large.UserLink-avatar {
  transform: scale(0.5); }

.App-main .Question-main .Question-mainColumn .QuestionInvitation .Card-header {
  height: 36px; }
  .App-main .Question-main .Question-mainColumn .QuestionInvitation .Card-header .Card-headerText {
    font-size: 10px !important; }
  .App-main .Question-main .Question-mainColumn .QuestionInvitation .Card-header .Card-headerOptions input {
    font-size: 10px !important; }

.App-main .Question-main .Question-mainColumn .QuestionInvitation .Button.QuestionMainAction {
  height: 35px;
  font-size: 10px !important; }

.App-main .Question-main .Question-mainColumn .QuestionInvitation .QuestionInvitation-content {
  min-height: auto; }

.App-main .Question-main .Question-mainColumn .QuestionMainAction {
  background: #f7f8fa; }

.App-main .Question-main .Question-mainColumn .List-header {
  margin: 0 12px;
  padding: 0; }
  .App-main .Question-main .Question-mainColumn .List-header .List-headerText,
  .App-main .Question-main .Question-mainColumn .List-header .List-headerOptions {
    font-size: 12px; }
    .App-main .Question-main .Question-mainColumn .List-header .List-headerText .Button,
    .App-main .Question-main .Question-mainColumn .List-header .List-headerOptions .Button {
      font-size: 12px; }

.App-main .Question-main .Question-mainColumn .List-item {
  padding: 20px 10px; }
  .App-main .Question-main .Question-mainColumn .List-item:not(:last-child):after {
    border-bottom: none; }
  .App-main .Question-main .Question-mainColumn .List-item .RichContent {
    font-size: 14px;
    line-height: 24px;
    margin-top: 10px;
    border-bottom: 1px solid #ebebeb; }
  .App-main .Question-main .Question-mainColumn .List-item .RichText p {
    margin-bottom: 0.5em; }

.App-main .Question-main .Question-mainColumn .Comments {
  background: #f7f8fa; }
  .App-main .Question-main .Question-mainColumn .Comments .CommentTopbar {
    background: #e7eaf0; }
    .App-main .Question-main .Question-mainColumn .Comments .CommentTopbar .CommentTopbar-meta {
      height: 47px; }
    .App-main .Question-main .Question-mainColumn .Comments .CommentTopbar .CommentTopbar-title,
    .App-main .Question-main .Question-mainColumn .Comments .CommentTopbar .CommentTopbar-switch {
      font-size: 13px; }
  .App-main .Question-main .Question-mainColumn .Comments .CommentItem {
    padding: 1em 1em .5em;
    font-size: 13px; }
    .App-main .Question-main .Question-mainColumn .Comments .CommentItem:not(:first-child):after {
      border-bottom: 1px solid #b8c1d3; }
    .App-main .Question-main .Question-mainColumn .Comments .CommentItem .CommentItem-meta,
    .App-main .Question-main .Question-mainColumn .Comments .CommentItem .CommentItem-content {
      font-size: 14px; }
    .App-main .Question-main .Question-mainColumn .Comments .CommentItem .CommentItem-time {
      font-size: 13px; }
    .App-main .Question-main .Question-mainColumn .Comments .CommentItem .CommentItem-content {
      line-height: 22px;
      margin-bottom: 0; }
    .App-main .Question-main .Question-mainColumn .Comments .CommentItem .CommentItem-footer .Button {
      font-size: 12px; }

.App-main .Question-main .Question-mainColumn .QuestionMainAction .AnswerAdd-header,
.App-main .Question-main .Question-mainColumn .AnswerAdd .AnswerAdd-header {
  background: #f7f8fa; }

.App-main .Question-main .Question-mainColumn .AnswerItem figure {
  width: 60%; }

.App-main .Question-main .Question-mainColumn .AnswerItem-meta.AnswerItem-meta--related {
  border-bottom: 1px solid #ebebeb;
  background: linear-gradient(to bottom, rgba(255, 255, 255, 0) 0%, #f7f8fa 100%); }

.App-main .Question-main .Question-mainColumn .QuestionAnswers-answerAdd .AnswerForm-editor {
  background-color: #fff; }
  .App-main .Question-main .Question-mainColumn .QuestionAnswers-answerAdd .AnswerForm-editor.is-focus {
    color: #4B3320;
    background-color: #F7F1E4; }

.App-main .Question-main .Question-sideColumn {
  opacity: 0.5; }
  .App-main .Question-main .Question-sideColumn:hover {
    opacity: 1; }
  .App-main .Question-main .Question-sideColumn .Card.Banner {
    opacity: 0.5; }
    .App-main .Question-main .Question-sideColumn .Card.Banner:hover {
      opacity: 1; }
}
