@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {
  div.dashboard-left div.trends { display: none; }  /* FUCK THIS */
       /*div.dashboard-left div.wtf-module, div.dashboard-right div.wtf-module { display: none; }  DON'T FUCK THAT */
       div.import-prompt { display: none;} /* FUCK THAT INSTEAD */
       div.Footer { display: none;}  /*  FUCK THOSE TOO*/
      [data-disclosure-type=”promoted”] { display: none; } /* FUCK ALL THESE */
       .LiveVideoHomePageModuleContainer { display: none; } /* FUCK THIS THING IN PARTICULAR */  
       .WtfLargeCarouselStreamItem {display: none;} /* ESPECIALLY FUCK THAT */
       .promoted-tweet{display: none;} /* MOREOVER FFFUCK THAT*/
       .has-recap{display: none;} /* I LIKE MY TWEETS ORDERED GODDAMIT */


  .wtf-module h3 {
    font-size: 0;
  }
  
  .wtf-module small {
    font-size: 0;
  }
  
  .Icon--smallest:before{
    content: "";
  }
  
  .module .flex-module {
    line-height: 0px;
  }

  .wtf-module h3:before {
    content: "Заходят как-то в бар";
    font-size: 18px;
  }

  .wtf-module .view-all {
    display: none;
  }
  
  .js-refresh-suggestions > small:nth-child(1){
    content: "Позвать новых";
    font-size: 0;
  }
  
    .js-refresh-suggestions > small:nth-child(1):before{
    content: "Позвать других";
    font-size: 11px;
  }

  .small-follow-btn>div{
    font-size: 0px;
  }
  
  div.UserSmallListItem > div > div > span > button.follow-text:after{
    content: "Угостить";
    font-size: 13px;
  }
  
  div.UserSmallListItem > div > div > span > button.following-text:after{
    content: "Угостили";
    font-size: 13px;
  }
  
  div.UserSmallListItem > div > div > span > button.unfollow-text:after{
    content: "Вышвырнуть";
    font-size: 13px;
  }
  
  div.UserSmallListItem > div > div > span > button.blocked-text:after{
    content: "Зобанен";
    font-size: 13px;
  }
  
  div.UserSmallListItem > div > div > span > button.unblock-text:after{
    content: "Помиловать";
    font-size: 13px;
  }
  
  div.UserSmallListItem > div > div > span > button.pending-text:after{
    content: "Ещё мутный";
    font-size: 13px;
  }
  
  div.UserSmallListItem > div > div > span > button.cancel-text:after{
    content: "НЕНЕНЕ Я ПЕРЕДУМАЛ";
    font-size: 13px;
  }
  
  div.UserSmallListItem > div > div > span > button > span{
    font-size: 0px;
  }
  
  .EdgeButton, .EdgeButton:visited, .avatar, .Avatar, .DashboardProfileCard-avatarImage, .global-nav .search-input, .input {
    border-radius: unset;
  }
  
  
}
