css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("explosm.net") {
  
  body {
    background-color: #111;
    color: #ddd;
  }
  
  .inner-wrap > .row {
    background-color: #222;
  }
  h1, h2, h3 ,h4 ,h5 ,h6 {
    color: #ffa70d;
  }
  
  .header-leaderboard-container, .footer-leaderboard-container {
    background-color: #333;
  }
  
  .box-container.-secondary {
    background-color: #666;
  }
  
  .past-week-comic-title a, .featured-comic-title a, .blog-title a {
    color: #3ba4bf;
}
  
  a {
    color: #3ba4bf;
  }
  
  a:hover {
    color: #ffa70d;
  }
  
  .author-credit-name, .comment-meta-data, .meta-data {
    color: #fff;
  }
  
  .custom-secondary-button {
    background-color: #1d4168;
  }
  
  #superheader-bar {
    background-color: #1d4168;
  }
  
  #footer {
    background-color: #1d4168;
  }
  
  #explosmore-callout-banner {
    background-color: #235082;
  }
  
  #legal-footer {
    background-color: #1d4168;
  }
  
  .site-header {
    background-color: #1d4168;
  }
  
  .site-header .search-bar input[type="text"], .site-header .search-bar input[type="search"], .header-cart-btn {
    background-color: #235082;
  }
  
  .site-header .search-bar input[type="text"]:hover, .site-header .search-bar input[type="search"]:hover, .header-cart-btn:hover {
    background-color: #ffa70d;
  }
  
  .site-footer {
    background-color: #222;
  }
  
  .nav-bar {
    background-color: #333;
  }
  
  .nav-bar a {
    color: #ccc;
  }
  
  .nav-bar a:hover {
    color: #ffa70d;
  }
  
  .site-nav--dropdown {
    background-color: #444;
  }
  
  .main-content h1, .main-content .h1, .main-content #shopify-product-reviews .spr-header-title, #shopify-product-reviews .main-content .spr-header-title, .main-content h2, .main-content .h2, .main-content h3, .main-content .h3, .main-content h4, .main-content .h4, .main-content h5, .main-content .h5 {
    color: #ffa70d;
  }
  
  a.product-grid-item, .product-grid-item {
    color: #3ba4bf;
  }
  
  a.product-grid-item:hover, .product-grid-item:hover {
    color: #ffa70d;
  }
  
  body, input, textarea, button, select {
    color: #fff;
  }
  
  #artistmenu {
    background-color: #666;
  }
  
  .pagination-custom > .active > a, .pagination-custom > .active > span, .pagination-custom > .active > a:hover, .pagination-custom > .active > span:hover, .pagination-custom > .active > a:focus, .pagination-custom > .active > span:focus {
    color : #fff;
  }
}
