css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("zestedesavoir.com") {
  
  .page-container, .main-container, body {
    background: white;
  }
  
  .main .content-container .subtitle {
    border: none;
  }
  
  .logbox .avatar {
    border-radius: 0px;
  }
  
  .logbox {
    background: transparent;
  }
  
  .sub-header {
    opacity: 0.1;
    background: white;
    transition: 0.3s all ease-in;
  }
  
  .sub-header:hover {
    opacity: 1;
  }
  
  .breadcumb::after {
    display: none;
  }

  .header-container {
    box-shadow: none;
  }
  
  .header-container header {
    border: none;
    box-shadow: none;
  }
  
  .main-container {
    background: white;
    text-align: justify;
  }
  
  .main .content-container header h1 {
    font-variant-caps: small-caps;
    border: none;
    color: black;
    font-weight: bold;
    font-size: 4em;
    margin: 10% 0px;
    line-height: 0.7em;
    padding: 20px;
    text-align: left;
  }
  
  .main .content-container h1, .main .content-container h2 {
    color: black;
    font-weight: bolder;
    border: none;
  }
  
  .badge {
    display: none;
  }
  
  .illu img {
    position: relative;
    border: none;
    border-radius: 100%;
    margin: 10%;
  }
  
  .topic-message .message::after, .topic-message .user::after {
    display: none;
  }
  
  .main .content-container header h1 img {
    margin-right: 25px;
  }
  
  .main .content-container .article-content h1,
  .main .content-container .article-content h2,
  .main .content-container .article-content h3,
  .main .content-container .article-content h3,
  .main .content-container .message-content h3  {
    color: black;
  }
  
  .main .sidebar {
    left: 0px;
    background: white;
  }
  
  .main .sidebar div h3 {
    border: none;
  }
  
  .avatar-link, .avatar {
    border-radius: 100%;
  }
  
  .main .content-container .article-content h2, .main .content-container .article-content h2 a {
    color: black;
    font-weight: bold;
    font-size: 1.5em;
    font-variant-caps: normal;
    border: none;
    margin: 0px;
    padding: 0px;
  }
  
  .article-content p {
    line-height: 2em;
  }
  
  .actions-title {
    opacity: 0.3;
    transition: 0.2s all ease-in;
  }
  
  .actions-title:hover {
    opacity: 1;
  }
  
  .main .content-container .comments-title {
    color: black;
    font-weight: bolder;
    border: none;
  }
  
  .featured-resource-item .featured-resource-meta h3 {
    color: white;
  }
  
  h2.ico-after::after {
    display: none;
  }
  
  h2.ico-after, .home .home-heading {
    padding-left: initial !important;
    margin-top: 40px;
  }
    
  .taglist li a {
    background: rgb(235, 235, 235);
    color: rgb(20, 20, 20);
    padding: 10px;
    margin: 5px;
    font-variant-caps: normal;
  }
  
  .taglist li a:hover {
    border: none;
  }
  
  .authors {
    border: none;
  }
  
  .authors .authors-label {
    display: none;
  }
  
  .page-footer {
    border: none;
    padding: 20px 0px;
    margin-top: 10%;
  }
  
  .pagination {
    border: none;
    background: white;
  }
  
  .pagination.pagination-top li a.current, .pagination li a.current {
    border: none;
    height: initial;
  }
  
  .topic-message .message .message-actions a.ico-after, .topic-message .message .message-actions button.ico-after, .topic-message .message .message-buttons .downvote.ico-after, .topic-message .message .message-buttons .upvote.ico-after, .topic-message .message .message-buttons a.ico-after, .topic-message .message .message-buttons button.ico-after, .topic-message .message .message-karma .downvote.ico-after, .topic-message .message .message-karma .upvote.ico-after, .topic-message .message .message-karma a.ico-after, .topic-message .message .message-karma button.ico-after, .topic-message .message .message-submit .downvote.ico-after, .topic-message .message .message-submit .upvote.ico-after, .topic-message .message .message-submit a.ico-after, .topic-message .message .message-submit button.ico-after {
    font-size: 0px;
    padding-left: 20px !important;
    border: none;
  }
  
  .topic-message .message .message-bottom .signature {
    border: none;
  }
  
  .topic-message .message .message-bottom {
    margin-top: 25px;
  }
  
  .logbox .dropdown .dropdown-list li:nth-child(2n+1), .logbox .dropdown .dropdown-list li:nth-child(2n+1) form button, .logbox .dropdown li:nth-child(2n+1), .logbox .dropdown li:nth-child(2n+1) form button {
    background: transparent;
  }
  
  .dropdown {
    border: none;
    box-shadow: none;
  }
  
  #open-my-account {
    display: none;
  }
  
  .logbox .notifs-links {
    margin: auto;
  }
  
  .main .content-container .article-content kbd, .main .content-container .message-content kbd {
    border: none;
    border-radius: 2px;
    background: #F6F6F6;
  }
  
  /* boutons */
  
  .btn-grey {
    background: transparent !important;
  }
  
  .btn-grey:hover {
    background: rgba(0, 0, 0, 0.1) !important;
  }
  
  /* boxes (error, info, warning, etc) */
  
  .main .content-container .article-content .error:not(.alert-box), .main .content-container .article-content .information:not(.alert-box), .main .content-container .article-content .question:not(.alert-box), .main .content-container .article-content .spoiler:not(.alert-box), .main .content-container .article-content .warning:not(.alert-box), .main .content-container .message-content .error:not(.alert-box), .main .content-container .message-content .information:not(.alert-box), .main .content-container .message-content .question:not(.alert-box), .main .content-container .message-content .spoiler:not(.alert-box), .main .content-container .message-content .warning:not(.alert-box) {
    padding: 15px;
  }
  
  .main .content-container .article-content .error:not(.alert-box)::after, .main .content-container .article-content .information:not(.alert-box)::after, .main .content-container .article-content .question:not(.alert-box)::after, .main .content-container .article-content .spoiler:not(.alert-box)::after, .main .content-container .article-content .warning:not(.alert-box)::after, .main .content-container .message-content .error:not(.alert-box)::after, .main .content-container .message-content .information:not(.alert-box)::after, .main .content-container .message-content .question:not(.alert-box)::after, .main .content-container .message-content .spoiler:not(.alert-box)::after, .main .content-container .message-content .warning:not(.alert-box)::after {
   background: none;
  }
  
  .main .content-container .article-content .error:not(.alert-box), .main .content-container .message-content .error:not(.alert-box) {
    border-left: 5px solid #B51C1C;
  }
  
  .main .content-container .article-content .information:not(.alert-box), .main .content-container .message-content .information:not(.alert-box) {
    border-left: 5px solid #1C80B5;
  }
  
  .main .content-container .article-content .warning:not(.alert-box), .main .content-container .message-content .warning:not(.alert-box) {
    border-left: 5px solid #E6A224;
  }

  .main .content-container .article-content .question:not(.alert-box), .main .content-container .message-content .question:not(.alert-box) {
    border-left: 5px solid #591CB5;
  }
  
  
  /* code */
  
  .codehilitetable, .codehilitetable .linenos, .codehilitetable tbody tr, .codehilitetable td {
    border: none !important;
  }
  
  .codehilitetable .linenos {
    border-right: none;
    background: white;
    -webkit-user-select: none;
  }
  
  .codehilite {
    font-family: "FiraCode", "Source Code Pro", "DejaVu Sans", "Roboto Mono", monospace !important;
    color: #254441;
    background: white;
  }
  
  .codehilite .nt, .codehilite .nf, .codehilite .nc {
    color: #256eff;
  }
  
  .codehilite .s2, .codehilite .s1, .codehilite .si, .codehilite .sb {
    color: #ff495c;
  }
  
  .codehilite .k, .codehilite .bp, .codehilite .kr {
    color: #4cc963;
  }
  
  .codehilite .kc, .codehilite .mi, .codehilite .nb {
    color: #f18805;
  }
  
  .codehilite .c1 {
    color: #586ba4;
  }
  
  .main .content-container .article-content li code, .main .content-container .article-content p code, .main .content-container .message-content li code, .main .content-container .message-content p code {
    color: #586ba4;
    background: transparent;
    border: none;
    padding: 0px 2px;
  }
  
}
