css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://freecodecamp.com"), domain("freecodecamp.com") {
  * {
    font-family: 'Fira Sans', serif;
    font-size: 1.1rem !important;
    line-height: 1.4rem !important;
  }
  
  body,
  .map-fixed-header {
    background-color: #2D3439;
    color: #ccc;
  }
  
  .topic-list > tbody > tr,
  .topic-list > tbody > tr:first-of-type {
    border: none;
  }
  
  html,
  .topic-list a.title {
    color: #C2EBFF;
  }
  
  a {
    color: #ace;
  }

  a:hover {
    color: #eee;
  }
  
  .blockquote {
    
  }
  
  .CodeMirror pre {
    font-family: 'Fira Mono', monospace;
  }
  
  cooked p {
    color: #DFE0E1;  
  }
  
  .topic-body {
    border-top-color: #393F4C;
  }
  
  .menu span {
    color: #3EAFE9;
  }
  
  h2.map-accordion-panel-title a,
  .night {
    background-color: #272B35;
    color: #EEE;
  }
  .mapWrapper,
  .map-aside-body,
  .map-fixed-header,
  .map-aside-frame {
    background-color: #393F4C !important;
  }
  
  code {
    background-color: #171B1F !important;
    border: 1px solid #2D3134 !important;
    border-radius: 3px;
  }
  
  .navbar,
  .d-header,
  .navbar ul {
    background-color: #393F4C;
  }

  iframe.iphone,
  .navbar ul a {
    background-color: #393F4C;
    font-size: 1.2rem;
  }
  
  .navbar ul a:hover {
    color: #393F4C;
    font-size: 1.2rem;
  }
  
  .btn-block,
  .btn-cta,
  .signup-btn.btn,
  .signup-btn,
  .btn-big,
  .btn-primary,
  .btn,
  .button {
    font-size: 1.2rem;
    background-color: #5675B9 !important;
    color: #CCC;
  }
  
  .CodeMirror-gutter-wrapper span {
    font-size: 1.2rem;
    line-height: 1.3rem;
  }
  
  .challenge-instructions > p {
    text-align: left;
    font-size: 1.1rem;
  }
  
  div.row #scroll-locker {
    overflow-y: scroll;
    padding: 1rem;
  }
}
