@font-face {
  font-family: 'Roboto';
  font-style: italic;
  font-weight: 700;
  src: local('Roboto Bold Italic'), local('Roboto-BoldItalic'), url(https://fonts.gstatic.com/s/roboto/v16/t6Nd4cfPRhZP44Q5QAjcC44P5ICox8Kq3LLUNMylGO4.woff2) format('woff2');
}


@media screen {

    body {
        font-size: 16px !important;
    }

    h1,
    div[data-is-title="true"] {
        font-family: 'Roboto', 'Helvetica Nenu', Helvetica, 'Microsoft YaHei', sans-serif !important;
        font-weight: 700 !important;
        font-size: 1.6em !important;
    }
    h2 {
        font-family: 'Roboto', 'Helvetica Nenu', Helvetica, 'Microsoft YaHei', sans-serif !important;
        font-weight: 400 !important;
        font-size: 1.4em !important;
    }
    span.ace-all-bold-hthree {
        font-family: 'Roboto', 'Helvetica Nenu', Helvetica, 'Microsoft YaHei', sans-serif !important;
        font-weight: 400 !important;
        font-size: 1.2em !important;
        line-height: 2.0em !important;
        padding-bottom: 10px !important;
    }
    span.ace-all-bold-hthree b {
        font-weight: 400 !important;
    }
    .ace-line,
    .ace-editor {
        font-family: 'Roboto', 'Helvetica Nenu', Helvetica, 'Microsoft YaHei', sans-serif !important;
        font-weight: 400 !important;
        font-size: 1.0em !important;
        line-height: 2.0em !important;
    }
    code,
    pre,
    .inline-code {
        font-family: 'Roboto Mono', 'Inconsolata', 'Courier New', 'Microsoft YaHei', monospace !important;
        font-size: 0.9em !important;
        line-height: 1.5em !important;
        font-weight: 400 !important;
    }
    .listtype-quote li {
        border-left-color: rgb(214, 219, 223) !important;
        border-left-style: solid !important;
        border-left-width: 10px !important;
        font-style: normal !important;
    }
    .highlight, .highlight.code, .highlight.inline-code {
        background-color: #ffff80 !important;
    }
}


@media print {

    body {
        font-size: 10pt !important;
    }

    h1,
    div[data-is-title="true"] {
        font-family: 'Helvetica Nenu', Helvetica, 'Roboto', 'Microsoft YaHei', sans-serif !important;
        font-weight: 700 !important;
        font-size: 1.6em !important;
    }
    h2 {
        font-family: 'Helvetica Nenu', Helvetica, 'Roboto', 'Microsoft YaHei', sans-serif !important;
        font-weight: 400 !important;
        font-size: 1.4em !important;
    }
    span.ace-all-bold-hthree {
        font-family: 'Helvetica Nenu', Helvetica, 'Roboto', 'Microsoft YaHei', sans-serif !important;
        font-weight: 400 !important;
        font-size: 1.2em !important;
        line-height: 2.0em !important;
        padding-bottom: 10px !important;
    }
    span.ace-all-bold-hthree b {
        font-weight: 500 !important;
    }
    .ace-line,
    .ace-editor {
        font-family: 'Helvetica Nenu', Helvetica, 'Roboto', 'Microsoft YaHei', sans-serif !important;
        font-weight: 400 !important;
        font-size:  1.0em !important;
        line-height: 1.5em !important;
    }
    code,
    pre,
    .inline-code {
        font-family: 'Inconsolata', 'Roboto Mono', 'Courier New', 'Microsoft YaHei', monospace !important;
        font-size: 1.0em !important;
        line-height: 1.5em !important;
        font-weight: 400 !important;
    }
    .listtype-quote li {
        border-left-color: rgb(214, 219, 223) !important;
        border-left-style: solid !important;
        border-left-width: 10px !important;
        font-style: normal !important;
    }
    .highlight, .highlight.code, .highlight.inline-code {
        background-color: #ffff80 !important;
    }
}
}
