}
.Tags__open .Tags__tag {
    box-shadow: 0 0 0 6px #57654e, 9px 0 0 6px #57654e, 18px 0 0 6px #57654e, 27px 0 0 6px #57654e;
}
.TimeEntryProject__createProject.TimeEntryProject__open>span, .TimeEntryProject__createProject:focus>span, .TimeEntryProject__trigger.TimeEntryProject__open .TimeEntryProject__itemSelected, .TimeEntryProject__trigger.TimeEntryProject__open .TimeEntryProject__noItemSelected, .TimeEntryProject__trigger:focus .TimeEntryProject__itemSelected, .TimeEntryProject__trigger:focus .TimeEntryProject__noItemSelected {
