    border-color: #b9b9b9;
    background-color: #505050;
    color: #bdbdbd;
}
.TagsPopdown__items a.TagsPopdown__selected {
    border-color: #a2c78d;
    background-color: #607356;
    color: #d4f7c6;
}
.TagsPopdown__items a.TagsPopdown__selected:focus, .TagsPopdown__items a.TagsPopdown__selected:hover {
    border-color: #fff;
    background-color: #6b9258;
    color: #d8ffc8;
}
.BillableSwitch__billable:focus .BillableSwitch__billableIcon:not(.BillableSwitch__disabled) {
