    color: #d8d8d8;
}
.ProfileMenu__items a.ProfileMenu__selected {
    background-color: rgba(75,200,0,.12);
    color: #d8d8d8;
}
.TimerModeSelector__mode {
    border-left: 1px solid #757575;
}
.Popdown__popdownDivider {
