    background-image: linear-gradient(90deg,rgba(250, 251, 252, 0) 0,rgba(250, 251, 252, 0) 10%,rgba(250, 251, 252, 0) 85%,rgba(250, 251, 252, 0));
}
.DayListItem__container {
    background-color: #3a3a3a;
