}
.boxes__floating {
    background-color: #484848;
    -webkit-box-shadow: 0 2px 8px 0 rgba(0,0,0,.26);
    box-shadow: 0 2px 8px 0 rgba(0, 0, 0, 0.26);
    border: 1px solid rgba(0, 0, 0, 0.4);
}
.TooltipPopdown__tooltip {
    color: #cecece;
}
.text__bodyText {
    color: #bfbfbf;
}
