}
.DayListItem__select.DayListItem__active>div:active {
    border-color: transparent;
    background-color: hsla(97,36%,65%,.12);
}
.DayListItem__select>div {
    background-color: #4e4e4e;
    border-color: #5f5f5f;
}
.left-nav {
    box-shadow: 0 2px 8px 0 rgb(0, 0, 0);
}
.GroupedEntryCounter__subEntriesCount span {
