css:
/* i really want this to be global */
.date-selector .date-button {
    position: absolute;
    top: 0;
    width: 18px;
    height: 18px;
    padding: 4px;
    font-size: 12px;
    line-height: 12px;
    color: #4078c0;
    cursor: pointer
}

.date-selector td {
    color: #4078c0;
    cursor: default;
    background: #fff;
    border: 1px solid #ccc
}

.date-selector td.selected,.date-selector td.selectable-day:hover {
    position: relative;
    z-index: 10;
    color: #fff;
    cursor: pointer;
    background: #4078c0;
    border-color: #33609a
}

.zeroclipboard-link {
    padding: 0;
    margin: 0;
    color: #4078c0;
    cursor: pointer;
    background: none;
    border: 0
}

a {
    color: #4078c0;
    text-decoration: none
}

.Box-row.Box-row--unread,.Box-row.unread {
    box-shadow: 2px 0 0 #4078c0 inset
}

.Box-row.navigation-focus .Box-row--drag-button {
    color: #4078c0;
    cursor: -webkit-grab;
    cursor: grab;
    opacity: 100
}

@media (min-width: 768px) {
    .Box-row-link {
        color:#333;
        text-decoration: none
    }

    .Box-row-link:hover {
        color: #4078c0;
        text-decoration: none
    }
}

.btn-outline {
    color: #4078c0;
    background-color: #fff;
    background-image: none;
    border: 1px solid #e5e5e5
}

.btn-outline .counter {
    background-color: rgba(0,0,0,0.07)
}

.btn-outline:hover,.btn-outline:active,.btn-outline.selected,.btn-outline.zeroclipboard-is-hover,.btn-outline.zeroclipboard-is-active {
    color: #fff;
    background-color: #4078c0;
    background-image: none;
    border-color: #4078c0
}

.btn-outline:hover .counter,.btn-outline:active .counter,.btn-outline.selected .counter,.btn-outline.zeroclipboard-is-hover .counter,.btn-outline.zeroclipboard-is-active .counter {
    color: #4078c0;
    background-color: #fff
}


.social-count:hover {
    color: #4078c0;
    cursor: pointer
}

.btn-link {
    display: inline-block;
    padding: 0;
    font-size: inherit;
    color: #4078c0;
    text-decoration: none;
    white-space: nowrap;
    cursor: pointer;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    background-color: transparent;
    border: 0;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none
}

.drag-and-drop-error-info a {
    color: #4078c0
}

a.tabnav-extra:hover {
    color: #4078c0;
    text-decoration: none
}

.filter-list.pjax-active .filter-item.pjax-active {
    color: #fff;
    background-color: #4078c0
}

.filter-item.selected {
    color: #fff;
    background-color: #4078c0
}

.subnav-item.selected,.subnav-item.selected:hover,.subnav-item.selected:focus {
    z-index: 2;
    color: #fff;
    background-color: #4078c0;
    border-color: #4078c0
}

.border-blue-dark {
    border-color: #4078c0 !important
}

.bg-blue {
    background-color: #4078c0 !important
}

.text-blue {
    color: #4078c0 !important
}

.link-gray:hover {
    color: #4078c0 !important
}

.link-gray-dark:hover {
    color: #4078c0 !important
}

.link-hover-blue:hover {
    color: #4078c0 !important
}

.muted-link:hover {
    color: #4078c0 !important;
    text-decoration: none
}
