@-moz-document url-prefix('http://'), url-prefix('https://') {

/* Revert Media

- Invert images and video. Intended for displaying media properly on an inverted display.

jamesmclendon.com */

img,
video,

/* Amazon */
.thumbnailPreviewTile,

/* Google Maps */
.app-imagery-mode, /* lightbox */
.widget-scene-imagery-iframe, /* lightbox */
.widget-runway-subview-card-view-container,

/* Slack */
.member_image, /* avatars */
.emoji-sizer, .emoji-wrapper, /* emoji */
.filetype_image, /* right sidebar thumbnails */
.image_container, /* image uploads */
.msg_inline_attachment_thumb_holder, /* web preview thumbnail */
.msg_inline_img_holder:not(.file_container_fixed_dimensions),

/* Twitter */
.tcu-imageWrapper,
.ProfileCard-bg,

/* Wikipedia */
.mw-mmv-overlay, /* lightbox */
.mw-mmv-pre-image, /* lightbox controls */
.mwe-popups-discreet, /* link previews */

/* YouTube */
.html5-endscreen,
.ytp-thumbnail-overlay-image,
.ytp-videowall-still,
.html5-video-player,
.html5-video-container,
#theater-background
{
  -webkit-filter: invert(1);
  filter: invert(1);
}

}
