css:
@-moz-document domain("usqstudydesk.usq.edu.au") {

#page.container-fluid {
	max-width: 980px;
}
  
/* make collapse-control visually more subtle */

.usq-collapseall-control {
	position: relative;
    margin-left: -2px;
    font-weight: 600;
    text-transform: uppercase;
    text-decoration: none;
    color: #d0d0d0;
    letter-spacing: 0.04em;
}

.usq-collapseall-control a {
	color: #b4b4b4;
    text-decoration: none;
    -webkit-transition: all .3s ease-in-out;
    -moz-transition: all .3s ease-in-out;
    -o-transition: all .3s ease-in-out;
    transition: all .3s ease-in-out;
}

.usq-collapseall-control a:hover {
    color: #7d7d7d;
}

.usq-collapse-hidden a:before,
.usq-collapse-toggler {
	opacity: 0.4;
}

/* section edits */

.course-content ul.usqflex li.section .content {
    margin: 0 22px;
}

.course-content ul.section {
    margin: 2em;
}

.course-content ul li.section.main {
    border-bottom: 1px solid #ddd;
}

.path-course-view .course-content .sectionname {
	margin: 30px 0;
}

.course-content ul.usqflex li.section {
	margin: 0;
}

.course-content ul.section {
	margin-top: 0;
}

/* activity icon size, hierarchy and alignment */


.activity img.activityicon {  /*scale*/
	transform: scale(0.8,0.8);
}

.course-content ul.section {  /*align left*/
	margin-left: 0;
}

.section .activity .contentafterlink {  /*move description closer to activity*/
	margin-top: 0;
}


.section li.activity {  /*activity spacing*/
	margin-top: 15px;
}



/* make left-hand-nav feel lighter */

.block_usq_coursenav .content a:after {
    content: none;
}

.block_usq_coursenav .content a.current:after {
    content: "" !important;
    opacity: 0.2;
	margin-right: 2px;
    margin-top: 9px
}

.block_usq_coursenav a.link-getstarted:before,
.block_usq_coursenav a.link-courseactivity:before,
.block_usq_coursenav a.link-forums:before,
.block_usq_coursenav a.link-assessments:before,
.block_usq_coursenav a.link-studyschedule:before,
.block_usq_coursenav a.link-teachingstaff:before,
.block_usq_coursenav a.link-resources:before,
.block_usq_coursenav a.link-calendar:before,
.block_usq_coursenav a.link-participants:before {
	transform: scale(0.8,0.8);
}

.block_usq_coursenav .content a {
	text-decoration: none;
}

/* bottom left hand side block (would have liked a bit more width and some padding */

.block_usq_coursenav .content li {
	border-bottom: none;
}

.block_search_forums .searchform {
    text-align: left;
}

#block-region-side-pre {
	border: solid 1px #d0d0d0;
    border-radius: 5px;
}

.section .activity .mod-indent-outer {
	padding-left: 0;
}

/* font-size line-height */

h2 {
	font-size: 30px;
    line-height: 40px;
    font-weight: 300;
    margin-bottom: 20px;
}


section p, section span, .topics li, .response, .contents ol li, .contents ul li {
	font-size: 18px !important;
	line-height: 30px !important;
}

section p {
    margin: 0 !important;
}

.instancename {  /* activity label */
	font-size: 20px;
}

/* how nice would it be to float the left hand nav to follow you as you scroll down the page? yes/no */

/* moodle book */

a.booknext {
	border: 1px solid grey;
    border-radius: 0 7px 7px 0;
    border-left: none;
    padding: 10px 40px 10px 40px;
    margin-top: 10px;
    -webkit-transition: all .3s ease-in-out;
    -moz-transition: all .3s ease-in-out;
    -o-transition: all .3s ease-in-out;
    transition: all .3s ease-in-out;

}

a.bookprev {
	border: 1px solid grey;
    border-radius: 7px 0 0 7px;
    padding: 10px 40px 10px 40px;
    margin-top: 10px;
    -webkit-transition: all .3s ease-in-out;
    -moz-transition: all .3s ease-in-out;
    -o-transition: all .3s ease-in-out;
    transition: all .3s ease-in-out;
}


a.bookprev:hover,
a.booknext:hover {
	background-color: #F7F7F7;
}

/* moodle quiz */

.ongoing {
	border: solid 1px #ccc;
    border-radius: 5px;
    padding: 8px;
    margin-bottom: 30px;
}

.singlebutton {
	margin-top: 15px;
}

.response {
	color: green;
}

/* page layout */

#page-content>section#region-main.has-sidebar {
    margin-left: -310px;
    padding-left: 310px;
}

#page-content>section.sidebar {
    width: 250px;
}

#block-region-side-pre {
	padding: 15px;
}

}
