css:
@-moz-document domain("www.magicbazar.fr") {
#my_banner div form input[type=submit] {
    background-color: #FF6161 !important;
    border: none !important;
    border-radius: 50px !important;
    box-shadow: 0 3px 15px 0 rgba(0,0,0,.25) !important;
    color: #fff !important;
    cursor: pointer !important;
    display: inline-block !important;
    font-weight: bold !important;
    height: 48px !important;
    margin-left: 1em !important;
    text-transform: uppercase !important;
    transition: all 0.5s !important;
    width: 80px !important;
}

#my_banner div form input[type=submit]:hover {
    background: #fff !important;
    box-shadow: 0 13px 15px 0 rgba(0,0,0,.25) !important;
    color: inherit !important;
    transform: translateY(-3px) !important;
}

#my_banner div form {
    text-align: left !important;
    width: 500px !important;
}

#my_banner > div:last-child {
    position: static !important;
    left: 0 !important;
    top: 0 !important;
    width: 500px !important;
}

#my_banner img, .banniereHeader {
    display: none !important;
}

#my_banner div {
    left: 0 !important;
    top: 1em !important;
    width: 100% !important;
}

#my_banner {
    background: none !important;
    position: fixed !important;
    height: 70px !important;
    width: 498px !important;
    top: 1em !important;
}

#quick_search_produits:focus {
    outline: none !important;
}

#quick_search_produits {
    background-color: #fff !important;
    border: none !important;
    border-radius: 50px !important;
    box-shadow: 0 3px 15px 0 rgba(0,0,0,.25) !important;
    cursor: pointer !important;
    display: inline-block !important;
    font-weight: bold !important;
    height: 48px !important;
    padding-left: 20px !important;
}

#navbar div:before {
    background-repeat: no-repeat !important;
    background-size: contain !important;
    content: "" !important;
    display: block !important;
    height: 40px !important;
    width: 45px !important;
}

#navbar div:nth-child(1):before {
    background-image: url(https://deckmaster.info/images/symbols/Q.png) !important;
    box-shadow: 0 3px 15px 0 rgba(0,0,0,.25) !important;
    width: 40px !important;
    border-radius: 50% !important;
}

#navbar div:nth-child(2):before {
    background-image: url(https://magidex.com/extstatic/symbol/set/DDR/c.svg) !important;
}

#navbar div:nth-child(3):before {
    background-image: url(https://magidex.com/extstatic/symbol/set/WWK/c.svg) !important;
}

#navbar div:nth-child(4):before {
    background-image: url(https://magidex.com/extstatic/symbol/set/GPT/c.svg) !important;
}

#navbar div:nth-child(5):before {
    background-image: url(https://magidex.com/extstatic/symbol/set/KTK/c.svg) !important;
}

#navbar div:nth-child(6):before {
    background-image: url(https://magidex.com/extstatic/symbol/set/RTR/c.svg) !important;
}

#navbar > div a {
    bottom: 0 !important;
    left: 0 !important;
    color: rgba(0,0,0,0) !important;
    position: absolute !important;
    right: 0 !important;
    text-shadow: none !important;
    top: 0 !important;
    height: 40px;
}

#navbar div {
    margin: 2em 0 0 3em !important;
    padding: 0 !important;
    position: relative !important;
}

#navbar {
    background: none !important;
    height: 70px !important;
    margin-left: 500px !important;
    position: fixed !important;
    top: 0 !important;
    width: 498px !important;
}

#place_panier {
    position: static !important;
    top: 0 !important;
    width: inherit !important;
    height: inherit !important;
}

#PanierDiv center span:nth-child(1), br:nth-child(2), br:nth-child(1) {
    display: none !important;
}

#PanierDiv {
    background: #fff !important;
    border: 0 !important;
    border-radius: 4px !important;
    position: fixed !important;
    right: 20px !important;
    top: 20px !important;
}

#col_gauche div {
    display: none !important;
}

#grosboutons {
    display: none !important;
}

#my_center, #news {
    margin-left: 0 !important;
    margin-right: 5% !important;
    width: 95% !important;
}

#news li:nth-child(2) tr:nth-child(2n) {
    opacity: 0 !important;
    height: 20px !important;
}

#news li:nth-child(2) tr td:nth-child(2) img, #news li:nth-child(2) tr td:nth-child(2) iframe {
    display: none !important;
}

#news li:nth-child(2) tr td:nth-child(1) img {
    float: left !important;
    width: 200px !important;
    margin: 0 !important;
}

#news li:nth-child(2) tr td:nth-child(2) {
    border-radius: 4px !important;
    background: white !important;
    padding: 10px !important;
}

#news li:nth-child(2) tr td:nth-child(1) {
    background: none !important;
}

#news li:nth-child(2) tr {
    overflow: hidden !important;
    position: relative !important;
}

#news_0 {
    display: none !important;
}

#news, #news_0 {
    background: none !important;
    border: none !important;
}

#contenu_tres_large p:last-child {
    padding-bottom: 10px !important;
}

#contenu_tres_large > p:first-child {
    background: 0 !important;
    margin-bottom: 20px !important;
    text-transform: uppercase !important;
    color: #fff !important;
}

#contenu_tres_large > * {
    background: #fff !important;
    color: #000 !important;
    margin-bottom: 0px !important;
    padding-top: 10px !important;
    padding-bottom: 0 !important;
    border-radius: 4px !important;
    text-align: center !important;
}

#contenu_tres_large {
    margin-top: 0 !important;
    background: none !important;
    border: 0 !important;
}

#col_droite > div:nth-child(3), #col_droite > div:nth-child(6) {
    display: none !important;
}

#col_droite > div:nth-child(2):before {
    content: "PANIER";
    width: 100%;
    padding: 10px 0;
    display: block;
    font-size: 14px;
    color: #000;
    background: #fff;
    border-radius: 0 0 4px 4px;
    
}

#col_droite > div:nth-child(2) > a {
    position: absolute !important;
    top: 0 !important;
    right: 0 !important;
    left: 0 !important;
    bottom: 0 !important;
}

#col_droite > div:nth-child(2) img {
    display: none !important;
}

#col_droite > div:nth-child(2) {
    background: 0 !important;
    border: 0 !important;
    position: relative  !important;
}

.hasJS {
    background-image: linear-gradient(to right, #ffe375 0%, rgba(255,97,97,0.8) 100%) !important;
    color: #5a6170 !important;
    font-family: 'Montserrat', sans-serif !important;
    font-weight: 300 !important;
    padding: 0 !important;
    margin: 85px 0 0 0 !important;
    text-align: center !important;
}


}
