css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("whatpulse.org") {
    span{
        color: white;
    }
    
    a{
        color: white;
    }
    
    label{
        color: black;
    }
    
    input{
        color: white;
    }
    
    input:hover{
        color: #66e6d9;
    }
    strong{
        color: black;
    }
    
    #whatpulse_search_str{
        background-color: #034867;
    }
    
    .rt-container-bg{
        background: transparent;
    }
    .rt-grid-4{
        height: 90px;
    }
    .centered{
        height: 80px;
    }
    #rt-header{
       height: 110px;
       margin-bottom: 13px;
       background-image: url("http://maxpixel.freegreatpicture.com/static/photo/1x/Laptop-Keyboard-White-Keyboard-Computer-Keyboard-1036970.jpg")!important;
       background-position: center center;
       background-size: cover;
       background-attachment: fixed;
       background-size: 80%;
    }
    #rt-header > div:first-child{
       background : transparent !important;;
       background-position: center center;
       background-size: cover;
       background-attachment: fixed;
       background-size: 80%;
    }
    .backgroundlevel-low{
       background-image: none;
       background: linear-gradient(to bottom, #000e14 0%,#034867 50%);   
       background-position: center center;
       background-size: cover;
       background-attachment: fixed;
       background-size: 80%;
    }
    #wp-menu ul li span{
        color: white;
        
    }
    #rt-navigation {
        height: 40px;
        margin-top: 10px;
        margin-bottom: -15px;
        margin-left: 5px;
    }
   
       
    #rt-navigation .rt-fusionmenu ul li a {
        height: 40px;
    }
    #rt-navigation .rt-fusionmenu{
        height: 40px;
    }
       
    #rt-navigation .rt-fusionmenu ul li a{
        height: 40px;
        background: linear-gradient(to bottom, #034867 0%,#000e14 70%);   
    }
    
    #rt-navigation .rt-fusionmenu ul li:hover a{
        height: 40px;
        background: linear-gradient(to bottom, #000e14 0%,#034867 70%);   
    }
    
    #rt-navigation .rt-fusionmenu ul .active a{
        height: 40px;
        background: linear-gradient(to bottom, #000e14 0%,#034867 70%);   
    }
    
    #rt-navigation .rt-fusionmenu ul li a span{
        height: 40px;
        display: inline-block;
        vertical-align: middle;
        line-height: normal;
    }
    
    .daddyicon{
        margin-top: -10px;
        background-image: none;   
    }
    
    .rt-fusionmenu {
        width: 1000px;
        background: linear-gradient(to bottom, #034867 0%,#000e14 70%); 
        padding: 0px;
        padding-left: auto;
        padding-right: auto;
    }
    .fusion-submenu-wrapper{
        margin-top: -20px;
    }
    
    .fusion-submenu-wrapper ul{
       height: 40px;
    }
    
    .fusion-submenu-wrapper ul li:first-child{
       height: 40px;
    }
    
    .fusion-submenu-wrapper ul li{
       height: 40px;
    }
   
    #rt-navigation .rt-fusionmenu ul li li:hover a{
        height: 40px;
        background: linear-gradient(to bottom, #034867 0%,#034867 70%);   
    }
    
    .rt-fusionmenu ul li a span{
        height: 40px;
    }
    
    .level2 ul li a:hover{
        background-color: #034867;
    }   
    
    .level2 ul li a{
        height: 40px;
    }
    
    .level2 ul li a span{
       height: 40px;
       display: inline-block;
       vertical-align: middle;
       line-height: normal;
    }
    .menutop li.root .level2 .daddyicon{
       background-position: -1000px 0 !important;
        background-image: none;
    }

    .readon{
        background: linear-gradient(to bottom, #034867 0%,#000e14 80%);   
    }
    #rt-footer-surround{
        background-color: #034867;
    }
    
    #rt-copyright{
        background-color: #000e14;
    }
    
    .component-content .rt-joomla-table th{
       background-color: #034867;
       color: white;
       font-weight: 800;
       font-size: 14px;
    }
    
    .component-content .rt-joomla-table tr{
       font-weight: 700;
       font-size: 12px;
    }
    
    .tabs-wrapper-small .wptabs{
        background-color: #034867;
    }
    
    .tabs-wrapper-small .wptabs li{
        background-color: transparent;
        margin-top: -2px;
    }
    
    .tabs-wrapper-small .wptabs li a{
        font-size: 15px;
        color: white;
        font-weight: 800;
        font-size: 14px;
    }
    .tabs-wrapper .wptabs{
        background-color: #034867;
    }
    
    .tabs-wrapper .wptabs li{
        background-color: transparent;
        width: 100px;
    }
    .wpbutton.save span{
        color: black;
    }
    
    .component-content .rt-joomla-table tr{
        height: 30px;
    }
    
    table span{
        color: black;
        font-size: 14px;
   }
    table label{
        color: black;
        font-size: 14px;
    }
    
    .rt-joomla-table.stats-table tr{
        height: 30px;
        font-size: 14px;
    }
    
    
}
