css:
@-moz-document domain("www.cplusplus.com") {
/********
Multiple Pages & stuff I've yet to categorize
********/

body {
    background: #141417;
}

#I_top {
    /* background: linear-gradient(to bottom, #4f4f4f 0%, #3a3a3a 100%); */
    
    background: #1B1B1F;
    outline: 1px solid black;
    height: 39px;
    margin-bottom: 25px;
}

#I_header {
    /* background: linear-gradient(to bottom, #4f4f4f 0%, #3a3a3a 100%); */
    
    height: 39px;
    background: #1B1B1F;
}

#I_corner {
    margin-left: 1px;
    border-left: 1px solid #000000;
    border-right: 1px solid #000000;
}

#I_cornerinner {
    background: linear-gradient(#34343B, #34343B 0%, #222226 100%, #222226);
    border: 0;
    width: 160px;
}

#I_search INPUT {
    background: #353538;
    border: 1px solid #102030;
}

#I_search INPUT.btn {
    background: #404045;
    border: 1px solid #102030;
}

#I_userbuttons {
    margin-left: 2px;
}

#I_userbuttons a {
    font-family: Arial;
    font-size: 13px;
    line-height: 24px;
    text-transform: capitalize;
    /*text-shadow: 0px -1px 0px #2f6627;*/
    /* background: linear-gradient(#123575, #123575 56%, #0D2654 56%, #0D2654); */
    
    background: #2C2C2E;
    border: 1px solid #102030;
    border-top-color: black;
    margin: -1px;
    width: 80px;
    border-radius: 2px;
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
    transition: background 100ms;
}

#I_userbuttons a:hover,
#I_userbuttons #I_btnlogin:hover {
    background: #313133;
}

#I_mid {
    background: #141417;
    color: #D9D9D9;
}

#I_main {
    margin-top: 9px;
    padding-right: 5px;
    background: #1F1F21;
    border: 1px solid black;
}

#I_content {
    padding-left: 3px;
    margin-top: 3px;
    margin-bottom: 3px;
}

#I_content h3 {
    color: #DDD;
}

A:link,
A:visited,
A:active {
    color: #2B72C7;
}

#I_nav .sect {
    background: #19191A;
    margin-right: 5px;
}

#I_nav .sect LI {
    background: #19191A;
}

#I_nav LI A SPAN {
    background: #19191A;
}

#I_nav LI A:hover,
#I_nav LI A:hover SPAN {
    background: #272729;
}

#I_nav .selected A,
#I_nav .selected A SPAN {
    background: #272729;
}

#I_nav .selected A:hover,
#I_nav .selected A:hover SPAN {
    background: #313133;
}

.C_deco {
    color: #D9D9D9;
}

.C_deco TR TD {
    background: #26262A;
    border: 1px solid #0F0F0F;
}

.C_deco TR.odd TD {
    background: #26262A;
}

.C_deco TR.even TD {
    background: #26262A;
}

#I_footer,
#I_bottom {
    background: #212124;
}

.C_pages A,
.C_pages SPAN {
    background: #7db7ff;
}

.C_pages A:link,
.C_pages A:vistited,
.C_pages A:active {
    color: black;
}

.qd b {
    background: #222224;
    color: #D9D9D9;
}

.C_doc SECTION H3:before {
    background-color: inherit;
}

.C_message {
    background: #19191a
}

.C_warning {
    margin: auto;
    color: #CCC;
    background: #4e1b1b;
    border-color: #310000;
}
/********
Frontpage
********/

.celltop {
    background: #17171A;
    border-bottom: none;
}

.bgi TD {
    /*background-color: #2F2F33; */
    
    background: #212124;
    color: #D9D9D9;
}
/******** 
Forums
********/
/* Threads */

.C_forThread .box {
    background: #313133;
    color: #CCC;
    border-color: #0F0F0F;
}

.C_forThread .top {
    background: #242424;
    color: #AAA;
}

.C_forThread .mid {
    background: #292929;
    color: #AAA;
}

.C_forThread .bot {
    background: #292929;
    color: #AAA;
}

.C_forThread .bot .auth {
    color: #666;
}
/* Posts */

.C_forPost .box {
    border: 1px solid #0E0E0F;
}

.C_forPost .boxtop {
    background: #222224;
    border-bottom: 1px solid #222224;
}

.C_forPost .dwhen {
    color: #D9D9D9;
}

.C_forPost .dwho {
    background: #1D1D1F;
}

.dwhat {
    background: #252526;
    color: #D9D9D9;
}

.C_forPost .dhow {
    background: #252526;
    color: #D9D9D9;
}

.C_forPost .dwhat .quote TD.qd {
    background: #222224;
    color: #CCC;
    border: none;
    border-left: 2px solid #1874ED;
    padding: 5px;
}

.C_btn,
A.C_btn {
    color: #111;
}
/******** 
Reference
********/

.C_doc DL.links {
    background: #16161A;
}

.C_doc .links DD {
    background: #1E1E1F;
}

.C_doc .links DT {
    background: none;
}

.C_doc #parameters DT {
    background-color: #2c2b2d;
}

.C_doc .C_prototype .even,
.C_doc .C_prototype .odd {
    background: #343437;
}

.C_doc .C_prototype TH {
    color: #37a937;
}

.C_doc .C_prototype TD {
    color: #ccc;
}

.C_doc .C_prototype TR {
    border-bottom: 1px solid;
}

section.description {
    color: #CCC;
}
}
