css:
@-moz-document domain("calendar.google.com") {
html {
	-webkit-filter: invert(90%) hue-rotate(180deg);
	filter: invert(90%) hue-rotate(180deg);
	background: rgb(25, 25, 25);
}
#onegoogbar a[href^="https://accounts.google.com"],
#onegoogbar a[href="javascript:_SR_backToCalendar();"] {
	-webkit-filter: invert(100%) hue-rotate(180deg);
	filter: invert(100%) hue-rotate(180deg);
}

.st-c-pos {
    font-family: Myriad Pro Condensed;
	font-size: 140%;
	padding: 4px;
	text-shadow: 1px 1px 0px white;
}

.st-c .te, .st-c .rb-n {
    line-height: 1;
}

.st-bg-today, .st-bg-td-first, .st-bg-td-last {
    background: yellow;
    border-left: 1px solid #666;
    -webkit-filter: invert(100%) hue-rotate(180deg);
}

.st-dtitle {
    line-height: 50px;
    font-size: 30px;
}

.st-dtitle-today {
	color: rgb(255, 73, 0);
    background-color: #ccc;
    text-shadow: 3px 3px 3px white
}
}
