css:
@-moz-document domain("www.kitarapaja.com") {
.product-item-grid { padding: 0em; }
.product-item-grid figure { display: block; min-height: 173px }
.product-item-grid figure img { float: left; width: 100%; height: auto; }
.product-list-info { padding: 0 10px }
.product-list-info h3, .actual-price { font-size: 13px }
.product-list-buy { text-align: center; padding: 0 5px 5px 5px; margin: 0 }
.product-list-buy .btn { width: auto !important; height: auto; margin: .5em 0; padding: 0.2em 0.5em; color: #800; border: 1px solid #800; border-radius: 4px; background: transparent; font-size: 11px; transition: all .2s; vertical-align: top; }
.product-list-buy .btn:hover { background: #800; color: #fff }
.product-list-buy .info-text { height: 31px; margin: 0; }
.is-not-sellable img { opacity: .3; -webkit-filter: grayscale(100%); filter: grayscale(100%); }
}
