css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(polygon.com) {
div.c-tab-bar.tab-bar-fixed,
li.c-global-header__link-more.use-fixed-dropdown,
div.l-col__sidebar,
div.c-river,
div.c-related-list,
footer.c-footer,
footer#site-footer,
footer.chorus-footer {
  display: none;
}


/* hide Facebook in nav bar & Pinterest picture badge & share menu */

.c-global-header__social > .has-no-sublist:nth-of-type(2),
a.c-pinterest-button,
ul.c-social-buttons,
ul.social-bar,
div.row.social-buttons,
div.m-grouptown__social {
  display: none !important;
}

section.l-segment.l-main-content {
  width: 978px !important;
  max-width: 100% !important;
  margin: 32px auto;
}

div.l-wrapper,
nav.c-global-header__links {
  border: 0 none;
}


div.l-chunk.chunk.chunk_a {
display: flex;
flex-direction: row;
flex-wrap: wrap;
}

div.l-chunk.chunk.chunk_a > :nth-child(2) {
margin-left: 0;
}

div.l-chunk.chunk.chunk_a > :last-child {
margin-right: 0;
}

div.l-chunk.chunk.chunk_a > * {
clear: both;
margin: 0 auto;
}



/* slightly increase article main picture size */

div.l-col__main figure.e-image.e-image--hero {
  margin-left: -16px;
  margin-right: -16px;
  margin-bottom: 64px;
}

span.e-image__meta {
  float: right;
  padding-left: 48px;
  padding-right: 32px;
  width: auto;
  /* text-align: right; */
}

div.p-fullbleed-block span.e-image__meta {
  float: none;
  width: auto;
  padding-left: 16px;
  padding-right: 16px;
  text-align: right;
}

.m-site-nav {
  opacity: 0.94;
}


/* center user login button */

div.m-site-nav__user-container.no-touch {
  margin-left: 3px;
}

img.m-site-nav__profile-thumb {
  margin-left: 0;
}

ul.c-global-header__social {
  padding-right: 12px;
  padding-left: 4px;
}

ul.c-global-header__social li a {
  margin: 0;
}

::selection {
  background: rgba(142, 43, 136, 0.4) !important;
}


/* +++ ARTICLEs +++ */

.c-entry-hero--feature .e-image__meta {
  max-width: 100%;
  padding: 8px 16px 0;
}


/* div .e-image { margin-bottom: 4.4rem; }
div.feature_sidebar .e-image { margin-bottom: 0; } */

div.m-share-buttons {
  visibility: hidden;
}

div.m-carousel {
  width: 100%;
  margin-left: 0;
}


/* +++ ADs +++ */

div.ad_chunk_a,
div.m-ad,
.ad_slidedown,
.m-ad__leaderboard,
div.intro_ad {
  display: none !important;
}

div.l-chunk.chunk.chunk_a .m-block--e .m-block__meta,
div.m-grouptown__header div.left_side div.m-block.m-block--e .m-block__meta {
  padding: 0 !important;
  /* always disable */
}

@media only screen and (min-width: 1100px) {
  div.l-chunk.chunk.chunk_a .m-block--e,
  div.m-grouptown__header div.left_side div.m-block.m-block--e {
    width: 100% !important;
  }
  div.l-chunk.chunk.chunk_a .m-block--e .m-block__meta,
  div.m-grouptown__header div.left_side div.m-block.m-block--e .m-block__meta {
    width: 620px !important;
  }
}

/* +++ COMMENTS +++ */
/* for color-coded comments install this UserScript using Tampermonkey / Greasemonkey: https://raw.githubusercontent.com/disrupted/MakeTheVergeGreatAgain/master/Color-Coded-Comments.user.js */
}
