css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("libhunt.com") {
  
  /*** Palette ***/
  :root {
    --default-bkdg-color: #282828;
    --bkgd-color-1: #202020;
    --bkgd-color-2: #333333;
    --bkgd-highlight: #16164c;
    --bkgd-error: #821616;
    --default-color: #aaaaaa;
    --border-color: #666;
    --blue-1: #1457bf;
    --blue-2: #1f6de7;
    --blue-3: #1662d6;
    --header-opacity: 0.7;
  }
  
  body {
    background-color: var(--default-bkdg-color);
    color: var(--default-color);
  }
  
  /***** X.libhunt.com where X in {cpp, java, android, python, go, rust, js, scala, js,...} *****/
  
  /* Header - 'Be informed', 'Submit',... row */
  .top-bar-wrapper {
    background-color: var(--default-bkdg-color);
  }
  
  .top-bar .menu li a:hover {
    color: var(--blue-2);
  }
  
  /* Colored 'Awesome X' heading */
  .heading-wrapper {
    opacity: var(--header-opacity);
  }
  
  /* Search input - 'Start hunting...' */
  input[type="text"], input[type="text"]:focus {
    background-color: var(--default-bkdg-color);
    color: var(--default-color);
  }
  
  .autocomplete-suggestions {
    background-color: var(--default-bkdg-color);
    color: var(--default-color);
  }
  
  .autocomplete-selected {
    opacity: var(--header-opacity);
  }
  
  /* Links */
  a {
    color: var(--blue-1);
  }
  
  a:hover {
    color: var(--blue-2);
  }
  
  /* Row items */
  .feed-item {
    border-top-color: var(--border-color);
    border-top-style: solid;
  }
  
  img[src="https://www.gravatar.com/avatar/ed65efb66c8e9d53797c1972de63ab33?s=80&d=retro"] {
    filter: invert(75%);
  }
  
  /* All categories - Left column */
  .categories li {
    background-color: var(--bkgd-color-1);
  }
  
  .categories li.highlighted {
    background-color: var(--bkgd-highlight);
  }
  
  .categories li:hover {
    background-color: var(--bkgd-color-2);
  }
  
  label {
    color: var(--default-color);
  }
  
  /* Privacy Msg, below left column */
  .sponsored .highlight {
    background-color: var(--default-bkdg-color);
  }
  
  /* Footer */
  .footer-wrapper {
    background-color: var(--bkgd-color-1);
  }
  
  a.logo > img {
    filter: invert(80%);
  }
  
  /***  /contribute Page ***/
  .callout.success {
    background-color: var(--bkgd-color-2);
  }
  
  .callout.alert {
    background-color: var(--bkgd-error);
  }
  
  /***  /jobs Page ***/
  .job-in-list {
    background-color: var(--bkgd-color-2);
    background-image: none;
  }
  
  .job-in-list .company-tagline {
    color: var(--default-color);
  }
  
  /*** /login Page ***/
  legend span {
    background-color: var(--default-bkdg-color);
  }
  
  input[type="email"], input[type="email"]:focus, input[type="password"], input[type="password"]:focus {
    background-color: var(--default-bkdg-color);
    color: var(--default-color);
  }
  
  /*** /categories ***/
  .popular-categories a.category:hover {
    color: var(--default-color);
    border-color: var(--border-color);
  }
  
  /*** /project/<project> ***/
  
  /* Overview/Tutorials */
  .tabs-nav-expanded a {
    background-color: var(--bkgd-color-2);
    color: var(--blue-3);
  }
  
  .tabs-nav-expanded a:hover {
    background-color: var(--bkgd-color-2);
  }
  
  .tabs-nav-expanded .selected a {
    background-color: var(--bkgd-color-2);
    border-bottom-color: var(--default-bkdg-color);
    color: #4c8ced;
  }
  
  /* Libhunt symbol */
  .carbonads-boxed {
    background-color: var(--default-bkdg-color);
  }
  
  .lib-list .rank {
    background-color: #363636;
  }
  
  .lib-list .lib {
    border-top-color: var(--border-color);
    border-top-style: solid;
  }
  
  /* Popular Comparisons */
  .comparisons-list .comparison a:hover {
    color: #7ba9f1; /* #367cea; */
    border-color: #93b0e0;
  }
  
  /* /tutorials e.g. caffe */
  .tutorials .tutorial .toc {
    background-color: var(--default-bkdg-color);
    color: var(--default-color);
  }
  
  .tutorials .tutorial .info, .tutorials .tutorial .comment {
    color: var(--border-color);
  }
  
  /** /recommend **/
  textarea, textarea:focus {
    background-color: var(--default-bkdg-color);
    color: var(--default-color);
  }
  
  .help-text {
    color: var(--border-color);
  }
  
  /** /suggest_changes **/
  
  /* dropdowns */
  select {
    background-color: var(--default-bkdg-color);
    color: var(--default-color);
  }
  
  /* Tags */
  .select2-container--default .select2-selection--multiple {
    background-color: var(--default-bkdg-color);
    border-color: var(--border-color);
  }
  
  .select2-container--default .select2-selection--multiple .select2-selection__choice {
    background-color: var(--bkgd-color-2);
  }
  
  .select2-results__options {
    background-color: var(--default-bkdg-color);
  }
  
  /*** /project/<project1>/vs/<project2> ***/
  
  /* Comparison Table */
  
  thead {
    background-color: #363636;
  }
  
  tbody {
    background-color: var(--default-bkdg-color);
    border-color: var(--bkgd-color-2);
  }
  
  tbody tr:nth-child(2n) {
    background-color: var(--bkgd-color-1);
  }
  
  /* Plot */
  .interest-over-time .embed iframe {
    background-color: var(--default-bkdg-color);
  }
  
  .interest-over-time .title a {
    color: var(--blue-1);
    border-bottom: none;
  }
  
  .interest-over-time .title a:hover {
    color: var(--blue-2);
    border-bottom-color: var(--blue-2);
    border-bottom-style: solid;
  }
  
  .interest-over-time .embed iframe rect {
    fill: #333 !important;
  }
  
  /*** /newsletter ***/
  .newsletter {
    background-color: var(--bkgd-color-1);
    background-image: none;
  }
  
  .newsletter .section-title {
    opacity: var(--header-opacity);
  }
  
  .newsletter .title {
    color: var(--default-color);
  }
  
  .newsletter .title:hover {
    color: var(--blue-3);
  }
  
  /* Subscribed/Unsubscribed message */
  .callout {
    color: var(--default-color);
  }
  
  /* /archive */
  thead {
    color: var(--default-color);
    border-color: var(--bkgd-color-2);
  }
  
  /* Subscribe E-mail form, on clicking 'Be informed' */
  .callout.primary {
    background-color: var(--bkgd-color-2);
  }
  
}
