    background-color: #222;
    border: 1px solid #333;
    border-radius: 1px;
    border-top-color: #333;
}
#fkbx:hover {
    background-color: #2a2a2a;
    border: 1px solid #3a3a3a;
    border-radius: 1px;
    border-top-color: #3a3a3a;
}
.thumb-ntp .mv-tile, .thumb-ntp .mv-empty-tile {
    background: #222 !important;
    border: 1px solid transparent !important;
    border-radius: 0 !important;
}
.thumb-ntp .mv-title {
    color: #fff;
}
.thumb-ntp .mv-x {
    background: #333;
}
#gb#gb a.gb_P, #gb#gb span.gb_P {
    color: #fff;
}
#gb#gb a.gb_b, .gbii {
	filter: invert(100%);
    -webkit-filter: invert(100%);
	-moz-filter: invert(100%)
}
#lga {
    filter: brightness(0)invert(1)opacity(.2);
	-webkit-filter: brightness(0)invert(1)opacity(.2);
	-moz-filter: brightness(0)invert(1)opacity(.2);
	transition: 0.5s ease;
	-webkit-transition: 0.5s ease;
	-moz-transition: 0.5s ease;
}
#lga:hover {
    filter: brightness(0)invert(1)opacity(.7);
	-webkit-filter: brightness(0)invert(1)opacity(.7);
	-moz-filter: brightness(0)invert(1)opacity(.7);
}
/* FOR GOOGLE CHROME USERS */
}

