css:
@-moz-document domain("io.hsoub.com") {
/*
DRAFT version
Blue-ish dark theme for io.hsoub.com
credits:
	Islam Ali, github:lambdax, io:iali
*/

body,
.white-bg,
div.v,
div.modal-content,
.userCardContainer,
.communityCard,
.lightBoxCommunity, .community-card,
.white,
.blockW,
.block,
.formBg,
#versionsModal .modal-body, 
#versionsModal .modal-body .version,
.diff li.ins,
.diff li.del
{
    background-color: #000 !important;
    color: #989898;
}
/*
.blockW {
    filter: invert(100%);
}*/
.email:hover {
    background: #222232;
}

hr {
    border-top: 1px solid #303030;
}

/* until the style attribute background value removed */

#HsoubFooterWrapper {
    display: none;
}
.itemsList .listItem {
    background-color: rgba(34, 34, 50, 0.2);
    border-right-color: rgba(34, 34, 50, 0.5);
    border-right-style: solid;
    border-right-width: 1px;
}
.post,
.singleAsk,
.reply,
.commentBox,
div[contenteditable=true],
.form-control {
    background-color: rgba(34, 34, 50, 0.2);
}
.hsoub-header {
   /* filter: grayscale(1.0);*/
}
.btn-link,
.btn-dummy-link,
a {
    color: #69533d;
}
.post-title a {
    color: #aaa;
}
.post-title a[href^=h] {
    color: #7676a0;
}
.post-title .postDetails a {
    color: #640408;
    color: #dd4814;
    color: #69533d;
}
/*
.block {
    filter: invert(100%);
}
.block img {
    filter: invert(100%);
}*/
.comment {
    background-color: rgba(34, 34, 50, 0.12);
    border-right-color: rgba(134, 134, 150, 0.2);
    border-right-style: solid;
    border-right-width: 1px;
    margin-bottom: 0px;
    margin-top: 8px;
    padding-bottom: 0px;
}
.post_vote .fa {
    color: rgb(89, 89, 138);
}
.postDetails>ul>li {
    border-left: 1px solid #3e3e3e;
}
.commentContent,
.replyContent,
.modal-body {
    border-top: 1px solid #3e3e3e;
}
blockquote {
    border-right: 1px solid #89442c;
}
.nav-tabs {
    border-bottom: 2px solid #222232;
}
.nav-tabs>li>a {
    background: none;
    border-bottom: 2px solid #202030;
}
.nav-tabs>li.active>a,
.nav-tabs>li.active>a:hover,
.nav-tabs>li.active>a:focus,
.nav-tabs>li>a:hover {
    color: #eee;
    background-color: transparent;
    border-bottom: 2px solid #323252;
    cursor: pointer;
}
.nav-tabs>li.active>a,
.nav-tabs>li.active>a:hover,
.nav-tabs>li.active>a:focus {
    color: #fff;
    background-color: #222232;
    cursor: default;
}
.btn-primary {
    background-color: #3c3c54;
    border-color: #3c3c54;
}
.articleCont h1,
.articleCont h1 a {
    color: #59598a;
    font-size: 22px;
    line-height: 1.5;
}
.postQuestion .postDetails {
    border-top: 1px solid #3e3e3e;
}
.articleCont h1.articleTitle {
    border-bottom: 1px solid #3e3e3e;
}
.commentBox {
    border-top: 1px solid #3e3e3e;
}
.profileBreif,
.infoBlocks .inf .contBlock,
.profileImg {
    background-color: #222232;
}
iframe {
    background-color: #000;
    position: relative;
}
iframe {
    filter: grayscale(1.0) brightness(0.2) opacity(0.2);
    transition: all .45s linear;
}
iframe:hover {
    filter: grayscale(0.0) brightness(0.8) opacity(1.0)
}
.communityCard,
.modal-content,
.userCardContainer,
.white {
    border: solid 1px #888;
}
.diff del strong {
    background-color: #000 !important;
    color: #989898;
    border-bottom: 3px solid #440000;
}
.diff ins strong {
    background-color: #000 !important;
    color: #989898;
    border-bottom: 3px solid #004400;
}


.latestComments li {
    border-bottom: 1px solid #3e3e3e;
}

.underline {
    border-bottom: 1px solid #3e3e3e;
}

.nav-tabs>li>a {
    color: #59598a
}

.nailthumb-container {
    border: 1px solid #E9E8E8;
    box-sizing: border-box;
    background: #000;
    margin-bottom: 10px;
    margin-top: 4px;
}

.dropdown-menu {
    background-color: #222232;
}


.nav .open>a, .nav .open>a:hover, .nav .open>a:focus {
    border-color: #59598a;
}

.dropdown-menu>li>a {
    color: #7676a0;
}
.dropdown-menu>li>a:hover, .dropdown-menu>li>a:focus {
    color: #aaaaaa;
    background-color: #3c3c54;
}

.hsoub-searchbar, .hsoub-searchbar .hsoub-search {
    background-color: #3c3c54;
    color: #ddd;
}

header.hsoub-header {
    background-color: #222232;
}
.hsoub-menu-item {
    background-color: #222232;
}

.hsoub-searchbar {
    border-bottom: 1px solid #3e3e3e;
}

no-touch .hsoub-menu-item-link:focus {
    background-color: #59598a;
}

.hsoub-no-touch .hsoub-menu-item-link:hover, .hsoub-no-touch .hsoub-menu-item-link:focus {
    background-color: #3c3c54;
}

.hsoub-menu-item.hsoub--active .hsoub-menu-item-link {
    background-color: #3c3c54 !important;
}

.hsoub-dropdown {
    background-color: #3c3c54;

}

.nav-tabs>li.active>a, .nav-tabs>li.active>a:hover, .nav-tabs>li.active>a:focus, .nav-tabs>li>a:hover {
    color: #3c3c54;
}


.hsoub-dropdown-item-link {
    color: #efefef;
}

.hsoub-bars {
    background-color:#222232;
}
.hsoub-no-touch .hsoub-bars:hover, .hsoub-no-touch .hsoub-bars:focus {
    background-color: #3c3c54;
}

.hsoub-list {
    background-color: #3c3c54;
    border: 1px solid #e8eaed;
}
.hsoub-list a {
    color:#efefef;
}
.hsoub-logo {
    background-color: #222232;
}
.nav-tabs>li.active>a, .nav-tabs>li.active>a:hover, .nav-tabs>li.active>a:focus, .nav-tabs>li>a:hover {
    color:#efefef;
}
}

@-moz-document url-prefix("https://io.hsoub.com/HsoubIO/57376") {
/* 
  for testing purpose by users in the theme post  
*/
.comment{
    border-top-color: rgba(134, 134, 150, 0.2);
    border-top-style: solid;
	border-top-width: 1px;
}
.commentContent {
    border-top: none;
}
}
