css:
@-moz-document domain("learn.lingvist.com") {
body, .app-wrapper, .app-background {
	background-image: url('https://airbusdefenceandspace.com/wp-content/uploads/2016/09/earth-view-from-satellite-space-systems-cover.jpg') !important;
    background-size: cover !important;
    background-repeat: no-repeat !important;
    background-color: #11111d;
  	background-position: center center !important;
}
header>nav>section>menu>ul>li>a>span.menu-text {
	color: lightblue;
}
section.context-translation-container>div.context-translations>div.translation-row>span.translation {
	color: lightblue;
}
article.card>section.main-card, .card {
	background: rgba(7,7,10,.85) !important;
    box-shadow: 0 3px 0.5rem #11122f;
    border-radius: 10px;
}
article.card>div.card-question>div.translations-container>div.translation-row>p>span.translation {
	color: lightblue;
}
article.card>section.extras>section header, article.card>section.extras>section main {
	background: rgba(7,7,10,.85) !important;
    box-shadow: 0 3px 0.5rem #11122f;
    color: lightblue;
}
section.app-footer>footer.milestone-progress>section.content-container>div.last-entry-container>p.last-entry-content>span.last-entry ,
section.app-footer>footer.milestone-progress>section.content-container>div.encouragement-container>p>em {
	color: lightblue;
}
section.app-footer {
	background-color: rgba(7, 7, 20, .75);
}
article.card>div.card-question>div.translations-container samp {
	color: lightblue;
}
}
