css:
@-moz-document domain("io.hsoub.com") {
.nav-tabs{
    border-bottom: 2px solid rgba(221, 72, 20, 0.10);
}
.nav-tabs>li>a{
    margin-left: 0px;
}
.nav-tabs>li.active>a, .nav-tabs>li.active>a:focus{
    color: #333333;
    background-color: transparent;
    border-bottom: 2px solid #dd4814 !important;
    cursor: pointer;
}
.nav-tabs>li.active>a:hover,.nav-tabs>li>a:hover{
    border-bottom: 2px solid rgba(221, 72, 20, 0.10);
}
.nav-tabs>li {
    bottom: -2px;
}
.nav-tabs>li>a{
    background: none;
    border-bottom: 2px solid rgba(221, 72, 20, 0.07);
}
.profileBreif,.block:not(.full_name),.itemsList .listItem,.post_vote,.row #post_details .card-shadow{
    background: white;
    box-shadow: 0 1px 1px rgba(0,0,0,0.25);
    border-radius: 1px;
    color: darkgray;
    margin: 3px;
    margin-bottom: 5px;
}
.row #post_details .card-shadow{
    color: black;
}
.post_vote{
    background: rgba(0,0,0,0.01);
}
.post .post_vote,#post-comments .post_vote{
    background: rgba(0,0,0,0);
    box-shadow: 0 0;
    margin: 0;
    margin-bottom: 0;
    border: 0;
}
.postDetails>ul>li{
    border-left: 2px solid rgba(0,0,0,0.035);
}
.postQuestion .postDetails{
    border: 1px solid rgba(0,0,0,0.035);
    background: rgba(0,0,0,0.025);
    display: flex;
    padding: 14px;
}
.block li,.cardContent{
    border: 1px solid rgba(0,0,0,0.035);
    background: rgba(0,0,0,0.025);
    display: block;
    padding: 10px;
}

.block li:last-child{
    margin-bottom: 10px !important;
}

.cardContent:after {
    content: "";
    display: table;
    clear: both;
}
.dropdown-menu .divider{
    height: 1px;
    background-color: rgba(0,0,0,0.01);
}
.dropdown-menu{
    border: 1px solid rgba(0,0,0,0.015);
}
.nav-tabs .dropdown-menu{
    margin-top: auto;
}
.hsoub-header{
    border: 1px solid rgba(0, 0, 0, 0.247059);
    box-shadow: rgba(0, 0, 0, 0.247059) 0px 1px 2px;
    height: 62px;
}
.btn{
    border-radius: 0px;
    border: 0px;
}
.select2-container--default .select2-selection--single, .select2-container--default .select2-search--dropdown .select2-search__field:focus,input,textarea,.post_area,.modal-content{
    border-radius: 0px !important;
}
.option input[type="checkbox"]+span:before{
    display:none;
}
.option input[type="checkbox"]{
	display: inline-block;
    transform: scale(1.3);
}

.modal-footer{
	border-top: 1px solid rgba(0,0,0,0.025);
    background: rgba(0,0,0,0.025);
}
.userCardHeaderText{
    width: auto;
}
}
