css:
@-moz-document domain("music.163.com") {

/*************************   尽量去掉收藏的滚动条    ***********************/

/* bg */
div.m-layer-w2 {
    width: 800px;
    height: 100%;
    margin: 115px -37px;
}

/* cont*/
div.m-layer div.zcnt {
    padding: 0;
    border: 1px solid #878787;
    border-width: 0 1px 1px;
    border-radius: 0 0 4px 4px;
    background: #fff;
    height: 100%;
}

/* cont size*/
div.m-layer .lyct-1 {
    padding: 0;
    height: 100%;
}

/* cont cont*/
.m-favgd ul li {
    padding: 6px 0 6px 35px;
    border-top: 1px solid #e0e0e0;
    float: left;
    width: 28%;
}

}
