css:
@-moz-document domain("www.pinterest.com") {
.TopLevelNav, div.tabItem.px2.py2.active{
	background-color: #1c1c1c;
}
.flex-wrap{
	padding-top:7%;	
}
.HomePage .Module{
	padding-top:1%;
}
.CampbellMenuList{
	background-color: #1c1c1c;
	border: gray 1px solid;
}
.CampbellMenuList .menuItem, .NavigateButton.linkModuleActionButton.btn.hasText, .Button, .Button.LikeButton.Module, .Button.DropdownButton.Module, a *, .closeupContentSection a, .PinActionBar.sticky a, .Module .PinActionBar a, .closeupContentSection h1,.closeupContentSection h2, .closeupContentSection h3,.closeupContentSection h4,.PinActionBar.sticky h1,.PinActionBar.sticky h2,.PinActionBar.sticky h3,.PinActionBar.sticky h4,.Module .PinActionBar h1,.Module .PinActionBar h2,.Module .PinActionBar h3,.Module .PinActionBar h4, .Button a, .Pin a, .creditName a *{
	-moz-transition: all .5s ease-in;
    -o-transition: all .5s ease-in;
    -webkit-transition: all .5s ease-in;
    transition: all .5s ease-in;
	color:white;
}
.Pin.summary:not(.hideHoverUI) .pinWrapper{
	-moz-transition: all .2s ease-in;
    -o-transition: all .2s ease-in;
    -webkit-transition: all .2s ease-in;
    transition: all .2s ease-in;
	color:white;
}
.CampbellMenuList .menuItem:hover, .Button:hover, div.tabItem.px2.py2.active div.tabText, a *:hover, .closeupContentSection h1:hover,.closeupContentSection h2:hover, .closeupContentSection h3:hover,.closeupContentSection h4:hover,.PinActionBar.sticky h1:hover,.PinActionBar.sticky h2:hover,.PinActionBar.sticky h3:hover,.PinActionBar.sticky h4:hover,.Module .PinActionBar h1:hover,.Module .PinActionBar h2:hover,.Module .PinActionBar h3:hover,.Module .PinActionBar h4:hover, .Button a:hover, .closeupContentSection a:hover, .PinActionBar.sticky a:hover, .Module .PinActionBar a:hover{
	color:red!important;	
}

.appContent{
	margin-top:-33px;
}
.Grid{
	margin-top:33px;	
}
.Closeup, .Grid, html, .closeupContentSection, .PinActionBar.sticky, .Module .PinActionBar,  .imageContainer, button.bg-white.no-border{
	background-color: #333!important;   
}
.fixedHeader.fixed.mxn3{
	background-color: #1c1c1c;
	opacity: 0.85;
}

.closeupContentSection,.PinActionBar.sticky,.Module .PinActionBar,.Pin.summary{
	color:lightgray!important;	
}
.closeupContentSection h1,.closeupContentSection h2, .closeupContentSection h3,.closeupContentSection h4,.PinActionBar.sticky h1,.PinActionBar.sticky h2,.PinActionBar.sticky h3,.PinActionBar.sticky h4,.Module .PinActionBar h1,.Module .PinActionBar h2,.Module .PinActionBar h3,.Module .PinActionBar h4, .savedTo a, .CloseupBottom .Module h3,.CloseupBottom .Module .richPinMetaLink .richPinGridTitle, .pinMeta p, .richPinMetaLink h3{
	color: #e0e0e0!important;
}

.CloseupBottom .Module .pinDescription, .CloseupSourceDescription .richPinInformation{
	border-width: 0px!important;
	color: lightgray;
}

.NavigateButton.linkModuleActionButton.btn.hasText, .Button, .Button.LikeButton.Module, .Button.DropdownButton.Module{
	color: white;
	background-color: #1c1c1c;
}
.NavigateButton.linkModuleActionButton.btn.hasText:hover, .Button:hover, .Button.LikeButton.Module:hover, .Button.DropdownButton.Module:hover, .Pin a:hover, .creditItem a *:hover{
	color:red!important;
}
.Button.footerIcon.borderless{
	background-color: white;	
}
.Pin.summary:not(.hideHoverUI) .pinWrapper:hover{
	background-color:#1c1c1c!important;
}
.Closeup .Module a:hover{
	background-color:transparent!important;	
}
.Button.Module.ShowModalButton.btn.hasIcon.hasText.isBrioFlat.medium.primary.primaryOnHover.repin.pinActionBarButton.isBrioFlat.rounded:hover, .Button.Module.ShowModalButton.btn.hasIcon.hasText.isBrioFlat.primary.primaryOnHover.repinSmall.rounded:hover{
	color:white!important;	
}
}
