css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("overthewire.org") {
    
    body {
        color: #dddddd;
        background-color: rgb(27, 27, 34)
    }

    a:link, a:active, a:visited {
        color: #dddddd !important;
    }

    #topmenu {
        color: #dddddd;
        background-color: rgb(21, 21, 26);
    }
    
    #topmenu ul li a {
        color: #dddddd;
    }

    #sidemenu ul li sh {
        font-family: 'Helvetica','Arial',sans-serif;
        color: #dddddd !important;
    }
    
    #sitename {
        font-family: 'Helvetica','Arial',sans-serif;
        color: #FFF;
    }
    
    #content h2 {
        color: #dddddd;
    }
    
    #content > div {
        border: solid 1px #28282d !important;
        background-color: rgb(35, 35, 40) !important;
    }

    div#title {
        color: #dddddd;
        background-color: rgb(27, 27, 34) !important;
        border-width: 0px !important;
    }
    /*
    #content {
        color: #dddddd !important;
        background-color: rgb(35, 35, 44) !important;
    }
    */
    
}
