css:
@-moz-document url-prefix("http://www.roblox.com"), url-prefix("https://www.roblox.com"), url-prefix("http://de.roblox.com"), url-prefix("https://de.roblox.com"), url-prefix("https://forums.roblox.com"), url-prefix("http://web.roblox.com"), url-prefix("https://web.roblox.com"), url-prefix("http://forums.roblox.com"), url-prefix("https://blog.roblox.com"), domain("roblox.com") {

/*ROBLOX New Logo Replacement by harule - https://www.roblox.com/users/17277708/profile*/

/*Should replace the new logo on the website with the old one*/



.icon-logo { /*Main site navbar full logo*/

    background-image: url(https://www.roblox.com/images/Logo/roblox_logo.svg) !important;

}



.icon-logo-r { /*Main site navbar small logo*/

    background-image: url(https://www.roblox.com/images/Logo/logo_R.svg) !important;

}



.big-logo { /*Download page logo*/

    background-image: url(https://www.roblox.com/images/Logo/roblox_logo.svg) !important;

    height: 80px !important;

}



.robloxLogo { /*Landing page small logo*/

	content: url("https://www.roblox.com/images/Logo/logo_R.svg") !important;

}



#MainLogoImage { /*Landing page big logo*/

    content: url("https://www.roblox.com/images/Logo/roblox_logo.svg") !important;

	margin-left: 20px !important;

    

}



#LogoAndSlogan .clearfix::after { /*Landing page slogan under the logo*/

    content: "Powering Imagination" !important;

    color: white !important;

    font-size: 35px !important;

    background-color: rgba(0,0,0,0.7) !important;

	padding: 10px 25px !important;

    text-align: center !important;

    margin-top: -20px !important;

    display: inline-block !important;

}



#LogoAndSlogan .clearfix::before { /*Slogan trademark thing, needed here so the font can be smaller*/

    content: "â„¢" !important;

    color: white !important;

    position: absolute !important;

    font-size: 15px !important;

    top: 100px !important;

    margin-left: 413px !important;

}



footer .comp-logo a { /*Blog footer logo*/

    background-image: url(https://images.rbxcdn.com/39ae3ca577c8488487ef492031b8e264.png) !important;

    background-size: cover !important;

    height: 30px !important;

}



header .site-title a { /*Blog header logo*/

    background-image: url(https://images.rbxcdn.com/39ae3ca577c8488487ef492031b8e264.png) !important;

    background-size: cover !important;

    height: 25px !important;

}

}
