css:
@-moz-document domain("app.lingualift.com") {
/*	  
     created and updated by    
       frederyk sperling
      http://frederyk.de/ 
  */

  /*	---------------	  */
  /*       CHANGELOG	  */
  /*	---------------	  */

  /*
  v0.1
  + fixed progress bar
  + fixed "well done" box (at end of review)
  + fixed review (vocabulary) page text
  + fixed kanji explanation box
  + added changelog to code

  v0
  + dark skin, first upload
  - style on messaging widget doesn't always work
  */

  /*	  ---------------	*/
  /*	  HOME/LOGIN PAGE	*/
  /*	  ---------------	*/

  /* HOME/LOGIN PAGE BACKGROUND COLOR*/
  .mdl-layout__content {
      background-color: black;
  }

  .mdl-layout__header {
      background-color: #0a0a0a;
  }

  .fill .fill.neutral {
      background-color: #0a0a0a;
      background: #0a0a0a;
  }

  .fill.success {
      background-color: #8c264a;
  }

  /* LIBRARY PAGE */
  .mdl-color-text--green {
      color: #ff4786 !important;
  }

  .mdl-color--green {
      background-color: #ff4786 !important;
  }

  /* "YOU ARE CORRECT" BOX*/
  .mdl-color--green {
      background-color: rgba(255, 71, 134, 0.5);
  }

  /* STREAK */
  .mdl-lift .usage .circles .circle.full {
      background: #ff4786;
  }

  .mdl-lift .usage .circles .circle {
      background: rgba(255, 255, 255, 0.17);
      border: 2px solid rgba(0, 0, 0, 0.3);
  }

  /*	  ---------------	*/
  /*	   SETTINGS PAGE	*/
  /*	  ---------------	*/

  /* SWITCHES */
  .mdl-switch__ripple-container .mdl-ripple {
      background: #ff4786;
  }

  .mdl-color--blue {
      background-color: rgba(255, 0, 0, 0.3) !important;
  }

  .mdl-color-text--blue {
      color: #ff4786 !important;
  }

  .mdl-color-text--amber {
      color: #f00 !important;
  }

  .mdl-color--amber {
      background-color: rgba(255, 0, 0, 0.7) !important;
  }

  .mdl-list__item--two-line .mdl-list__item-primary-content .mdl-list__item-sub-title {
      color: rgba(255,255,255,0.7);
  }

  .mdl-button {
      color: #fff;
      background-color: rgba(0, 0, 0, 0.3) !important;
  }

  /*	  ---------------	*/
  /*	   LEARNING PAGE	*/
  /*	  ---------------	*/

  /* PROGRESS BAR */
  .mdl-progress>.bufferbar {
      background-image: linear-gradient(to right,rgba(255,255,255,.3),rgba(255,255,255,.3)),linear-gradient(to right,rgba(255,255,255,.3),rgba(255,255,255,.3));
  }

  .mdl-progress>.progressbar {
      background-color: #ff4786;
      z-index: 1;
      left: 0;
  }

  /* PAGE BACKGROUND COLOR*/
  .mdl-grid {
      background-color: transparent; /* #060606 */
  }

  /* CARD BACKGROUND COLOR */
  .mdl-card {
      background-color: #111;
  }

  /* CARD TEXT COLOR */
  .mdl-color-text--grey-900 {
      color: #c4c4c4 !important;
  }

  .mdl-card__content-text>p:first-of-type::first-letter {
      color: #ff4786;
  }

  /* QUESTION SUBJECT TEXT */

  .activity .mdl-card .prompt .task {
      color: #ddd;
  }

  .mdl-color-text--black {
      color: #fff !important;
  }

  /* CARD BOX */
  .box.box- {
      color: #fff !important;
      background-color: #0a0a0a;
  }

  /* KANJI CHARACTERS */
  .hanChar:hover {
      color: #ff4786;
  }

  .popbar .mdl-card {
      padding: 20px;
      padding-top: 15px;
      padding-bottom: 15px;
      background: rgba(0,0,0,0.9);
      min-height: 60px;
  }

  /* CARD AUDIO SYMBOLS */
  .mdl-button.mdl-button--colored {
      color: rgb(255,64,129);
  }

  /* CARD TUTOR BOX */
  .box.box-orange {
      color: #fff !important;
      background-color: #0a0a0a;
  }

  .mdl-card__content-text .box h4 {
      color: #ff4786;
  }

  /* SIDEBAR TEXT COLOR */
  .mdl-list__item {
      color: rgba(255,255,255,.87);
  }

  /* ACTIVE SIDEBAR TEXT COLOR */
  .sidebar .mdl-list__item.active .mdl-list__item-primary-content, .sidebar .mdl-list__item.active .mdl-list__item-primary-content i {
      color: #ff4786;
  }

  .sidebar .mdl-list__item.status-done {
      color: #ff4786;
  }
  .sidebar .mdl-list__item.status-done i {
      color: #ff4786;
  }

  /* BUTTONS */
  .mdl-color-text--green-500 {
      color: #ff4786 !important;
  }

  .mdl-color--green-500 {
      background-color: rgba(0,0,0,0.56) !important;
  }

  /* MESSAGING WIDGET */

  #intercom-container .intercom-launcher .intercom-conversations-header .intercom-comment-container-user .intercom-comment{
      background: #ff4786;
      background-color: #ff4786;
  }
}
