css:
@-moz-document domain("www.anime-sensei.net") {
body {
    background: #1e1e1e !important;
}

a {
    text-shadow: 0px 0px 0px #000 !important;
}

.post {
    background: #373737 !important;
    color: #dfdfdf !important;
}

.zannafar {
    color: #46890b;
}

table {
    background-image: url(http://i.imgur.com/NpmY3gf.png) !important;
    background: transparent !important;
}

#menu {
    background: #3d3d3d !important;
}

#pages li a {
    text-shadow: 0px 0px 0px #000 !important;
}

.inner.box:nth-of-type(2) {
    display: none !important;
}

.box.search {
    margin-top: 5px !important;
}

.post {
    border: 1px solid #919191 !important;
}

.box.inner {
    background: #373737 !important;
}

.post h2,
.box h2 {
    background: #1c1c1c !important;
    text-shadow: 0px 0px 0px #000 !important;
    border-bottom: 1px solid #3f8f35 !important;
}

.postmetadata {
    border-top: 1px solid #3f8f35 !important;
}

.search .searchinput,
#searchform {
    background: #1c1c1c !important;
    color: #dfdfdf !important;
}

.search .searchbtn {
    color: #6eb035 !important;
    background: #373737 !important;
    text-shadow: 0px 0px 0px #000 !important;
    border-left: 1px solid #919191 !important;
}

.box {
    border: 1px solid #919191 !important;
}

#notice #progress {
    background: #1c1c1c !important;
    border: 1px solid #919191 !important;
}

.ui-widget-header {
    background: #193500 !important;
}

#notice #progress .ui-widget-header {
    border: 1px solid #377600 !important;
}

#pages li a.current:hover,
#pages li a.current,
#pages li a:hover {
    -webkit-box-shadow: 0px 0px 0 #000 !important;
    box-shadow: 0px 0px 0 #000 !important;
    background: #1c1c1c !important;
    color: #6eb305 !important;
    border: 1px solid #919191 !important;
}
}
