css:
@-moz-document domain("twoship.com") {
#g-page-surround .g-container {
    margin: 0;
}
section#g-mainbar {
    background: #fff !important;
}
section#g-container-main div.g-content * {
    font-family: sans-serif !important;
    font-size: inherit !important;
}
section#g-container-main div.g-content table tr td {
    color: #444 !important;
}
div#pagecontent,
div#undersiderubrik {
    background: none !important;
}
section#g-container-main div.g-content table {
    width: 100% !important;
    border: none;
}
section#g-container-main div.g-content table tr td {
    border: none;
}
section#g-container-main div.g-content table tbody tr td table tbody tr th,
section#g-container-main div.g-content table tbody tr td table tbody tr td,
section#g-container-main div.g-content div#undersiderubrik table tbody tr th,
section#g-container-main div.g-content div#undersiderubrik table tbody tr td {
    border: 1px solid #dedede;
}
section#g-container-main div.g-content table table td:nth-child(3) {
    text-align: right;
}
section#g-container-main div.g-content h2 {
    font-size: 1.5rem !important;
}
section#g-container-main div.g-content table.tableStandard td {
    padding: 0 !important;
}
section#g-container-main div.g-content table tr td.tableStandardCol_1 table tr td {
    padding: 0.5rem !important;
}
div#g-copyright {
    background: rgba(255, 255, 255, 0.5);
    color: #999999;
}
}
