css:
@-moz-document domain("dict.pl") {
html, body {
    background-color: #1C2833;
    color: #D6DBDF;
    font-family: Lato;
    font-size: 12px;
    margin: 0;
    padding: 0;
}
a {
    color: #607D8B;
    font-family: Lato;
    font-size: 12px;
    text-decoration: none;
}

#topHome {
    width: 744px;
    margin: 5px auto 5px auto;
}
#top {
    /*width: 506px;*/
    width: 744px;
    margin: 5px auto 5px auto;
}
#mainBackground {
    margin-top: 0px;
    padding: 10px;
    background-color: #34495E;
    border: solid 1px #1b4f72;
    text-align: center;
    clear: both;
}
#mainHome{
    padding: 0px;
    margin-bottom: 10px;
    background-color: #273746;
    text-align: left;
}
#main{ /* other properties futher */
    padding: 8px;
    margin-bottom: 15px;
    background-color: #f9f9f9;
    text-align: left;
}

/* OTHER ELEMENTS */
#main LI {
    padding-top:10px;
}
H1{
    font-size: 15px;
    font-weight: bold;
    text-align: center;
}
H2{
    font-size: 13px;
    font-weight: bold;
    text-align: center;
}
H3{
    font-size: 13px;
    font-weight: normal;
    text-align: center;
}
IMG { border: 0 }
SMALL { font-size: 10px; }
/* SIMPLE LIST */
.list {
    padding-right: 20px;
}
.list LI{
    text-align: justify;
}
.list A{
    text-decoration: underline;
}
/* SEARCHTOOL */
#searchTool{
    width: 744px;
    overflow: hidden;
    position: relative;
}
#searchToolHeader{
    width: 718px;
    background-color: #34495E;
    position: relative;
    border: solid 1px #1b4f72;
    padding-top: 4px;
    padding-bottom: 4px;
    padding-left: 12px;
    padding-right: 12px;
}
#searchToolHeader A {
    color: #D6DBDF;
    font-family: Lato;
    font-size: 11px;
    text-decoration: none;
    font-weight: bold;
}
#searchToolBody{
    font-weight: bold;
    background-color: #273746;
    border: solid 1px #1b4f72;
    border-top: none;
    padding-top: 4px;
    padding-bottom: 16px;
    padding-left: 12px;
    padding-right: 12px;
    text-align: left;
}
#searchToolBody IMG{
    margin-right: 3px;
}
#searchToolBody FORM{
    margin: 0px;
    margin-top: 5px;
    padding: 0px;
}
#specChar {
    width: 83%;
    text-align: right;
    padding: 4px 0px;

}
#specChar IMG{
    float: left;
}
ul.searchToolSpecialChar {
    list-style: none;
    display: inline;

}
ul.searchToolSpecialChar LI {
    display: inline;
    white-space: nowrap;

}
ul.searchToolSpecialChar A{
    font-family: Lato;
    font-size: 11px;
    font-weight: bold;
    color: #1F1F1F;
    padding: 4px 6px;
}/*
ul.searchToolSpecialChar {
float: right;
}
ul.searchToolSpecialChar, ul.searchToolSpecialChar LI {
    list-style: none;
    display: block;
    margin: 2px;
    padding: 0px;
    text-align:center;
}
ul.searchToolSpecialChar LI {
    float: left;
}
ul.searchToolSpecialChar A{
    display: block;

    font-family: Lato;
    font-size: 11px;
    font-weight: bold;
    color: #1F1F1F;
    padding: 4px 2px;
}*/
ul.searchToolSpecialChar A:hover{
    background-color: #123D83;
    color: #D6DBDF;
}
#searchToolInputText{
    margin-right: 7px;
    font-size: 19px;
    background-color: #607d8b;
    border: solid 1px #1b4f72;
    width: 89%;
}
#searchToolButtonSimple, #searchToolButtonBrackets {
    vertical-align: bottom;
    cursor: pointer;
}

/*  BANNERS          description of banners in banners.txt  */
#advCorner{  /* right top corner, home and results page */
    position: absolute;
    bottom: 0px;
    right: 0px;
}
#advBelowMain { /* bottom of main part, home and results page */
    overflow: hidden;
    margin-bottom: 15px;
    clear: both;
    text-align: center;
}
#advHorizontal { /* above main part, results page */
    clear: both;
    text-align: center;
    background-color: #f9f9f9;
    border: solid 1px #1b4f72;
    margin-top: 4px;
}

/* BOTTOMLINKS */
#bottomLinks {
    width: 100%;
    background-color: #123D83;
    position: relative;
    padding: 4px 0px;
    font-size: 10px;
    text-align: center;
    display:none;
}
#bottomLinks A {
    color: #D6DBDF;
    font-family: Lato;
    font-size: 10px;
    text-decoration: none;
    font-weight: bold;
    padding: 0px 6px;
    white-space: nowrap;
}
#bottomLinks A:hover { color: #e3ecfb; }

/* FLOATING CORNERS */
.lt {
    position: absolute;
    left: 0px;
    top: 0px;
    z-index: 1;
    display: none;
}
.rt {
    position: absolute;
    right: 0px;
    top: 0px;
    z-index: 1;
    display: none;
}
.lb {
    position: absolute;
    left: 0px;
    bottom: 0px;
    z-index: 1;
    display: none;
}
.rb {
    position: absolute;
    right: 0px;
    bottom: 0px;
    z-index: 1;
    display: none;
}

/* USEFULL INFO*/
ul.usefullInfo {
    list-style: none;
    display: block;
    margin: 2px;
    padding: 0px;
    width: 200px;
}
ul.usefullInfo LI {
    display: block;
    padding: 2px;
}
ul.usefullInfo A{
    display: block;
    font-family: Lato;
    font-size: 12px;
    font-weight: bold;
    text-align: left;
    color: #000000;
    background-color: #e3ecfb;
    padding: 5px;
}
ul.usefullInfo A:hover{
    border: 1px dotted blue;
    padding: 4px;
}
ul.usefullInfo B {
    color: rgb(18, 61, 131);
}
ul.usefullInfo STRONG {
    color: red;
}
ul.usefullInfo EM {
    font-size: 11px;
    font-weight: normal;
    color: rgb(18, 61, 131);
    text-decoration: none;
    font-style: normal;
}

/* MAIN BOTTOM LINKS */
.mainBottomCopyright {
    float: left;
    text-align: left;
    width: 34%;
    vertical-align: middle;
    display: none;
}
.mainBottomCounter {
    float: left;
    text-align: center;
    width: 100%;
    vertical-align: middle;
}
.mainBottomInterface {
    float: right;
    text-align: right;
    width: 32%;
    vertical-align: middle;
    display: none;
}
.mainBottomCopyright, .mainBottomCopyright A, .mainBottomInterface {
    font-size: 11px;
    color: #123D83;
    text-decoration: none;
    font-weight: bold;
}
.mainBottomCounter  A {
    font-size: 12px;
    text-decoration: none;
}
.mainBottomCopyright IMG, .mainBottomCounter IMG, .mainBottomInterface IMG {
    margin-left: 4px;
}

/* NEWS */
.news {
    color: #5F5F5F;
    padding: 3px 3px 10px 3px;
    text-align: left;
}
.news DIV { /* news header*/
    padding: 0px 0px 2px 0px;
    font-weight: bold;
}
.news A{
    color: #000000;
    text-decoration: underline;
}

/* WORDS OF WEEK */
.words_of_week {
    font-family: Lato;
    font-size: 15px;
    color: #e3ecfb;
    width: 100%;
    text-align: left;
    margin: 5px;
}
.words_of_week_topic TD {
    color: #e3ecfb;
    font-size: 15px;
    font-weight: bold;
    padding: 0px 0px 12px 0px;
}
.words_of_week A {
    font-size: 13px;
}
/* RESULTS */
.resTable {
    width: 100%;
    border: none;
    background-color: #34495E;
    text-align: center;
}
.resTable TH {
    background-color: #212F3D;
}
.resTable TH, .resTable  TD{
    padding: 2px;
}
.resWordCol {
    text-align: left;
    width: 43%;
}
.resRow {
    background-color: #2C3E50;
}
.resRowHover {
    background-color: #273746;
}
.resHighlight {
    color: black;
    background: red none repeat scroll 0%;
/*  -moz-background-clip: -moz-initial;
    -moz-background-origin: -moz-initial;
    -moz-background-inline-policy: -moz-initial;*/
}
.notFoundInfo {
    /* width:780px; */
    background-color: #273746;
    padding-top: 34px;
    padding-bottom: 60px;
    text-align: center;
    line-height: 20px;
}
.notFoundInfo  A{
    font-weight: bold;
    text-decoration: underline;
}
.notFoundInfo TABLE{
    margin: 5px auto;
    text-align: left;
}
.notFoundInfo  TD{
    padding: 2px 5px;
}
.notFound2ndCol {
    text-align:center;
}
/* LETTERS LINK */
ul.letters_link {
    list-style: none;
    margin: 0;
    margin-top: 7px;
    padding: 5px;
    text-align: center;
}
ul.letters_link LI {
    display: inline;
    white-space: nowrap;
}
ul.letters_link A{
    font-size: 13px;
    padding: 0px 4px;
}
ul.letters_link A:hover{
    background-color: #c1c1c1;
}

/* IRREGULAR VERBS */
.irregularVerbs, .irregularVerbsDep {
    text-align: left;
    margin: 10px auto;
}
.irregularVerbs {
    width: 70%;
}
.irregularVerbs TH, .irregularVerbsDep TH {
    font-weight: bold;
    padding-top: 10px;
}
.irregularVerbs A:hover, .irregularVerbsDep A:hover {
    background-color: #dddddd;
}
/* STATISTICS */
#statisticsImg {
    padding-bottom: 20px;
    text-align: center;
}

/* IN WEB */
.inWeb A{
    font-size: 13px;
    font-weight: normal;
    text-decoration: none;
}
.inWeb A:hover{
    text-decoration: underline;
}
.inWeb  DT{
    margin: 10px 20px;
    font-weight: bold;
}

/* IMPRINT */
#imprint {
    position: relative;
    width: 100%;
}
#imprintText {
    width: 66%;
}
#imprintText STRONG{
    font-size: 12px;
    font-weight: bold;
}
#imprintText A{
    text-decoration: underline;
}
#imprintContact {
    position: absolute;
    top: 0px;
    right: 0px;
    background:#ebebeb;
    padding: 5px;
    vertical-align: top;
    width: 205px;
}
#imprintContact A {
    text-decoration: underline;
    font-size: 12px;
}
#imprintContact P{
    padding: 0px;
    margin: 0px;
    font-size: 14px;
    font-weight: bold;
}
#imprintVersion2 {
    position: absolute;
    bottom: 0px;
    right: 0px;
    font-size: 9px;
    color: #5F5F5F;
    padding: 5px;
}
#imprintVersion {
    float: right;
    font-size: 9px;
    color: #5F5F5F;
}
#imprintBasedLinks {
    width: 80%;
    margin-top: 10px;
    text-align: left;
}
#imprintBasedLinks A{
  font-size: 10px;
  color: #5F5F5F;
  text-decoration: none;
}
.imprintBasedLinksCol1 {
    width: 20px;
    text-align: center;
}

#ramka {  /* test */
    width:506px;
    font-weight: bold;
    background-color: #123D83;
    color: #D6DBDF;
    font-family: Lato;
    font-size: 11px;
    text-decoration: none;
    position: relative;
    padding-top: 3px;
    padding-bottom: 3px;
    padding-left: 12px;
    padding-right: 12px;
}

/* RECOMMEND US */
.recommendUs {
    margin: 10px auto;
}
.recommendUs TD{
    padding: 5px;
}
.ru1stCol {
    text-align: right;
}
#recommendationButton {
    width: 100px;
    margin-top: 10px;
    margin-left: 55px;
}
/* ADVERTISE */
.advImg {
    text-align: center;
    margin: auto;
}
.advImg IMG {
    width: 250px;
}

/* ASSIST BOOK */
.assistBook {
    width: 85%;
    text-align: right;
    margin: 10px auto;
}
.assistBook TD, .assistBook TH {
    font-family: Lato, Arial, Helvetica;
    font-weight: normal;
    font-size: 12px;
    color: #000;
    padding: 0px 5px;
}
.assistBook TH {
    font-weight: bold;
    padding: 5px 5px;
}
.abRank {
    width: 40px;
}
.abName {
    text-align: left;
}
.abWords {
    width: 40px;
}
/* TEACHERS INTERPRETORS */
.servicesLinks {
    margin-bottom: 30px;
}
.servicesLinks LI{
    padding-bottom: 5px;
}
.servicesLinks A{
    font-weight: bold;
}
.servicesLinks A:hover{
    text-decoration: underline;
}
P.justBlock {
    text-align: justify;
    margin: 0px;
}
.justBlock A {
    text-decoration: underline;
}
.servicesList DL, .servicesList P{
    margin: 20px 0px 40px 0px;
    padding: 0px;
}
.servicesList DT{
    margin: 20px 0px;
    padding: 0px;
    font-weight: bold;
}
.servicesList DD{
    margin: 5px 20px;
    padding: 0px;
}
.servicesList A{
    color: rgb(51, 51, 255);
}
.tiBottomLink {
    margin: 30px 0px 3px 0px;
}
.tiBottomLink A{
    text-decoration: underline;
}
/* FORUM */
.forumTitle {
    padding: 7px;
    float: left;
}
.forumTitle A{
    color: rgb(18, 61, 131);
    font-weight: bold;
    font-size: 14px;
}
.forumLink {
    float: right;
    color: rgb(18, 61, 131);
    font-weight: bold;
    font-size: 11px;
}
.forumLink  IMG{
    vertical-align: middle;
}
.forumPost {
    padding-left: 10px;
    padding-bottom: 5px;
    font-size: 11px;
    color: #5F5F5F;
}
.forumUser {
    padding-left: 8px;
    padding-bottom: 5px;
    font-size: 11px;
    color: #5F5F5F;
    font-weight: bold;
}
.forumDate {
  font-size: 9px;
  color: #5F5F5F;
}
/* FIRST PAGE LAYOUT */
.p1_table {
    /* table-layout: auto; */
    width: 100%;
    border: none;
}

.p1_horizontalLine{
    background-color: red;
    padding: 0px;
    height: 0px;
}
.p1_verticalLine{
    background-color: red;
    padding: 0px;
    width: 0px;
}
.p1_dictName {
    /*width: 30%;*/
    text-align: center;
    font-family: Lato;
    font-size: 25px;
    font-weight: bold;
    color: #607d8b;
}
.p1_dictTitle {
    text-align: left;
    padding-left: 4px;
    font-family: Lato;
    font-size: 20px;
    font-weight: bold;
    color:  #607d8b;
}
/* ADLINKS BOTTOM */
.adlinksbox {
    margin: 10px auto 5px auto;
    display: none;
}

.adlinks a {
    text-decoration: underline;
}

#specChar ul li a {
    color: #a9c3dc;
}

#searchToolBody img:last-child {
    margin-right: 0;
}

#specChar {
    width: 90%;
}

a:hover {
 	color: #a9c3dc;
}

#searchTool,
#mainBackground {
  margin-top: 10px;
}

#searchToolInputText {
  margin-right: 7px;
  font-size: 17px;
  background-color: #212f3d;
  border: solid 1px #34495e;
  color: #a9c3dc;
  width: 85%;
  padding: 6px 15px;
}

:focus {
	outline-color: #47708c;
}

#searchToolBody IMG {
  margin-right: 3px;
  position: relative;
  top: -4px;
}

ul.searchToolSpecialChar A:hover {
  background-color: #212f3d;
  color: #D6DBDF;
}

#specChar > img {
 	display: none;
}

#searchToolButtonSimple {
 	content: url("http://www.alfifood.com/.images/search-icon.svg"); 
	height: 25px;
  width: 27px;
}

img#searchToolButtonBrackets {
 	content: url('http://www.crwflags.com/fotw/images/g/gb-1606.gif');
  height: 21px;
  width: 26px;
  position: relative;
  top: -7px;
  margin-left: 2px;
}

#advHorizontal #mainBackground {
 	margin: 0 
}

#advHorizontal {
 	margin-top: 10px 
}

.resHighlight {
  color: #ff9393;
  background: none;
}

.resWordCol a font {
 	color: #a9c3dc !important;
  background: none !important;
}

img[src="images/google.gif"] {
  content: url("https://cdn4.iconfinder.com/data/icons/new-google-logo-2015/400/new-google-favicon-128.png"); 
  height: 13px;
  width: 13px;
}

img[src="images/pronunciation1.gif"],
img[src="images/pronunciation2.gif"],
img[src="images/pronunciation3.gif"] {
 content: url("https://cdn4.iconfinder.com/data/icons/basic-ui-colour/512/ui-33-128.png");
  height: 13px;
  width: 13px;
}

img[src="images/def.gif"] {
 content: url("http://www.ich-suche-ein-buch.de/theme/main/images/icon-buch.png");
 height: 13px;
  width: 13px;
}

tr:nth-of-type(5),
tr:nth-of-type(6), 
tr:nth-of-type(7),
tr:nth-of-type(8),
tr:nth-of-type(9) {
 	display: none; 
}

#topHome div:nth-of-type(3) {
  display: none;
}

#mainHome table {
  padding: 15px 0px; 
  padding-bottom: 18px;
}

#mainHome a {
 	font-size: 14px; 
}

#mainHome .resTable td {
 	padding: 6px 10px; 
}
}
