css:
@-moz-document domain("makeuseof.com") {
html,
body,
.article-card-caption-container,
.header-navigation-mega,
.single-post-sidebar::before,
.single-post-sidebar,
.single-post-sharing-button::after,
.single-post-sharing-button::before,
.publisher-makeuseof .navbar-nav>li>.dropdown-menu {
	background-color: #181818 !important;
}
.dark-feature .article-card-caption-container {
	background-color: transparent !important;
}
.header-wrap,
.header-search-form,
.publisher-makeuseof .navbar-default,
.cart-overlay.cart-open .cart {
	background: #202020 !important;
	box-shadow: 0 0 15px #000000 !important;
	border-bottom: 2px solid rgba(0, 0, 0, .7) !important;
	text-shadow: 0px 0px 2px #000 !important;
}
.header-branding,
.article-card-taxonomy-container,
.navbar-brand .icon-publisher-logo-name {
	filter: grayscale(100%) invert(100%) brightness(30%);
	-webkit-filter: grayscale(100%) invert(100%) brightness(30%);
}
.header-navigation .menu-item.selected a::after,
.header-navigation-mega-items .menu-item.selected a::after {
	filter: grayscale(100%) invert(0) brightness(300%);
	-webkit-filter: grayscale(100%) invert(0) brightness(300%);
}
.article-triple-feature .article-card-taxonomy-container {
	filter: none !important;
	-webkit-filter: none !important;
}
body * {
	border-color: transparent !important;
	color: #888 !important;
	background-color: transparent !important;
	text-decoration: none !important;
}
.article-card-taxonomy,
.header-navigation-mega-search-posts .article-card .article-card-taxonomy,
.single-post-transition-label {
	background-color: #e22524 !important;
}
.article-post-grid .article-card-taxonomy,
.article-triple-feature .article-card-taxonomy,
.single-post-simple-header .article-card-taxonomy,
.size-small .article-card-taxonomy,
.single-post-hero-image-container .article-card-caption-container {
	background-color: transparent !important;
}
a {
	text-decoration: none !important;
	color: #ccc !important;
	text-shadow: 0px 0px 2px #000 !important;
	transition: color .4s;
}
a:hover {
	text-decoration: none !important;
	color: #fff !important;
	transition: color .4s;
	text-shadow: 0 0 4px #000, 0 0 2px #666 !important;
}
.article-card-caption::after,
.deals-feature-item-container::after,
.single-post-sidebar::after,
.article-card.align-right::after,
.article-card.size-medium.position-relative .article-card-relative-image .article-card-relative-image-container::after,
.author-header::after,
.category-header::after,
.header-navigation-mega-posts .meganav-post-thumbnail::after,
.single-post-featured-image-container .single-post-featured-image::after,
.single-post-hero-image-container .single-post-hero-overlay::after,
.size-medium.position-relative .article-card-relative-image-container::after,
.size-small .article-card-relative-image-container::after {
	border-color: transparent transparent transparent #181818 !important;
}
.link-callout,
.header-navigation-mega,
.nav-filters-desktop>li.active>a {
	border-color: #353535 !important;
}
.header-tools-search-submit svg,
.header-tools-search-open svg {
	fill: #b9b9b9 !important;
}
.single-post-sharing-button {
	background-color: #353535 !important;
}
.single-post-transition-bar {
	filter: grayscale(100%) invert(88%) brightness(69%);
	-webkit-filter: grayscale(100%) invert(88%) brightness(69%);
}
textarea,
input[type="text"],
input[type="email"],
input[type="password"],
input[type="number"] {
	border-bottom: 1px solid rgba(255, 255, 255, .18) !important;
	border-radius: 3px !important;
	box-shadow: inset 0px 1px 5px 0px #000 !important;
	border-top-color: #000 !important;
	border-right-color: #111 !important;
	border-left-color: #111 !important;
	background-color: #141414 !important;
}
.img-with-overlay .overlay {
	background-color: black !important;
	color: white !important;
}
.btn,
.comment-reply a,
.comments-list-toggle {
	background-color: #202020 !important;
	text-shadow: 0px 0px 2px #000 !important;
	box-shadow: 0px 0px 4px 0px #000 !important;
}
}
