css:
@-moz-document url-prefix("http://www.goatlings.com/battle/challengers") {
img[src*="enemies"] {
 width: 35px;  
 height: 35px;
}

tr {
 height: 40px;   
}

form {
     line-height:5px;   
}

#content input[type="submit"] {
    padding: 6px 0px;
    border: 3px solid #bc9c71; /* Button outline color! */
    border-radius: 5px;
    width: 100%;
    background-color: #a17a50; /* Button color! */
    font-size: 12px;
    color: #f8f8c4; /* Font color! */

    }

#content input[type="submit"]:hover {
    background: #865f37;
     -moz-transition:background .1s ease-in;
    -o-transition:background .1s ease-in;
    -webkit-transition:background .1s ease-in;
    border: 3px solid #9e774f;
    cursor: pointer
}


td select {
    border: 1px solid #d5b46b; /* Button outline color! */
    border-radius: 5px;
    width: 100%;
    background-color: #ffffcd; /* Button color! */
    font-size: 12px;
    color: #4A2A1F; /* Font color! */

    text-align: center;
    
}
}

@-moz-document url-prefix("http://www.goatlings.com/battle/thebattle") {
/* Feel free to remove this if you don't want the bigger weapon and end battle buttons! */

a[href*="over"] {
padding: 25px 10px;
 background-color: #ffffcd; /* Button color! */
    border: 5px solid #d5b46b; /* Button outline color! */
    border-radius: 5px;
    color: #4A2A1F; /* Font color! */
    font-size: 16px;
}

a[href*="over"]:hover {
padding: 25px 10px;
 background-color: #a17a50; 
    border: 5px solid #bc9c71;
    border-radius: 5px;
    color: #f8f8c4;
    font-size: 16px;
}

table {
 margin: 40px 0px 0px 0px;   
}

img[src*="enemies"] {
width: 150px;    /* Enemy size! */
}

table img[src*="pets"] {
 width: 150px;   /* Goatling size! */
}

.itema img {
 width:150px;   /* Weapon size! */
}
}

