css:
@-moz-document domain("zhihu.com") {
ul.HomeEntry-list,
div.zg-wrap.zu-main.clearfix {
    width: 100%;
}
div.zu-main-content-inner {
    width: 70%;
    padding: 0 15% 0 15%;
}
#zh-question-list {
    padding-right: 15px;
}
li.HomeEntry-item {
    padding: 0 0;
    width: 33%;
    text-align: center;
}
div.zu-main-sidebar,
.HomeEntry-draft {
    display: none !important;
}
div.zh-backtotop {
    margin-left: 700px;
}
/*以上*/

body.zhi {
    padding-top: 55px;
    color: #34495e;
    line-height: 2;
    font-size: 14px;
    font-family: Monaco, Menlo, Microsoft Yahei;
}
body.zhi h2 {
    font-size: 18px;
    font-weight: 700;
}
.zm-item-vote {
    left: -72px;
    top: 16px;
}
.zm-item-feed .zm-item-vote {
    left: -50px;
    top: 8px;
}
/*WTF*/

.zu-top {
    background-image: none;
    border-bottom: none;
    background-color: #34495e;
    padding: 5px 0;
}
.zu-top-add-question {
    background-image: none!important;
    border-radius: 3px;
    border: 1px solid #4e6d8d;
    box-shadow: none!important;
    opacity: .9;
    -ms-filter: "alpha(Opacity=90)";
}
.zu-top-add-question:hover {
    opacity: 1;
    -ms-filter: "alpha(Opacity=100)";
}
.zu-top-search-input {
    border-radius: 3px;
    border: none;
    box-shadow: none;
}
.top-nav-dropdown li:last-child>a {
    border-radius: 0 0 3px 3px;
}
.top-nav-dropdown li:first-child>a {
    border-radius: 3px 3px 0 0;
}
*[class*="zg-btn"] {
    border-radius: 3px;
    background-image: none;
    box-shadow: none;
}
.zu-top-nav-li.current {
    background-image: none;
    background: #34495e;
    color: #16a085;
}
.zu-top-search-input:focus {
    background-color: #293a4a;
    color: #fff;
    border: none;
    box-shadow: none;
}
.zu-top-nav-link,
.zu-top-nav-link:visited,
.zu-top-nav-link:active {
    color: #fff;
    opacity: .8;
    -ms-filter: "alpha(Opacity=80)";
}
.zu-top-nav-link:hover,
.current .zu-top-nav-link,
.current .zu-top-nav-link:visited,
.current .zu-top-nav-link:active {
    opacity: 1;
    -ms-filter: "alpha(Opacity=100)";
}
a {
    color: #ac3e40;
}
a:hover {
    text-decoration: underline;
}
.zu-top-search-input {
    color: #536a81;
    background: #293a4a;
}
.zu-top-nav-userinfo.selected,
html.no-touch .top-nav-profile:hover .zu-top-nav-userinfo {
    background: #34495E;
    border-radius: 3px;
}
.zu-top-nav-userinfo:hover {
    background: #536a81;
}
.top-nav-dropdown li a {
    border-top: 1px solid #2f4154;
}
.top-nav-profile a {
    background: #34495E;
}
.top-nav-profile a:hover {
    background: #293a4a!important;
}
.top-nav-profile .top-nav-dropdown {
    padding-top: 10px
}
html.no-touch .top-nav-dropdown a:hover {
    background: #34495e;
}
.zm-side-nav-link.active,
html.no-touch .zm-side-nav-roundtable .follow-link:hover+.zm-side-nav-link,
html.no-touch .zm-side-nav-link:hover {
    background: #ecf0f1;
    border-radius: 3px;
}
.zg-wrap {
    width: 1100px;
    margin: 0 auto;
    clear: both;
}
html.no-touch .zm-votebar .up:hover,
.zm-votebar .up.pressed {
    background: #2980b9;
    color: #fff;
}
html.no-touch .zm-votebar .down:hover,
.zm-votebar .down.pressed {
    background: #ac3e40;
    color: #fff;
}
.zm-votebar {
    margin-left: -72px;
}
.feed-item .avatar,
.feed-item .avatar a,
.feed-item .avatar img {
    width: 75px;
    height: 75px;
}
.feed-item .avatar img {
    border-radius: 3px;
}
.feed-main,
.zm-item-answer {
    margin-left: 90px;
}
.zh-backtotop {
    margin-left: 400px;
}
.origin_image {
    height: auto !important;
}
.zg-btn-blue {
    background: #34495e;
    border-radius: 3px;
}
.zg-btn-green,
.zg-btn-follow {
    color: #fff !important;
    background: #34495e;
    border: #34495e;
    border-radius: 3px;
    opacity: .9;
    -ms-filter: "alpha(Opacity=90)";
}
.zg-btn-green:hover,
.zg-btn-follow:hover {
    opacity: 1;
    -ms-filter: "alpha(Opacity=100)";
}
.zm-item-tag {
    border-radius: 3px;
}
.zm-item-tag:hover {
    background: #34495E !important;
}
.zu-top-search-button {
    background-image: none !important;
    border-radius: 3px;
    background: #4e6d8d;
    border: none !important;
    box-shadow: none !important;
}
.modal-dialog-title {
    box-shadow: none;
    background-image: none;
    border: none;
    background: #34495e;
    padding: 1;
}
.zm-votebar.version-text .up.pressed {
    background: #34495e;
}
.zm-votebar.version-text .up.pressed:hover {
    background: #4e6d8d !important;
}
.zm-topic-cat-main li a {
    border-radius: 3px;
}
html.no-touchevents .zm-topic-cat-main li:hover a,
.zm-topic-cat-main li.current a {
    background: #34495e;
    border-color: #34495e;
    color: #fff;
}
.item-collapse.is-sticky {
    background-color: #ac3e40;
}
.zm-side-section #js-reg-with-sina-in-top {
    background: #e55345;
}
.goog-buttonset-default {
    background: #34495e;
    border: none;
}
.zg-btn-white:hover {
    background: #eee;
}
.zm-item-answer-owner .zm-item-vote-count {
    color: #fff;
    background: #ac3e40;
}
.SidebarListNav-hint {
    font-size: 12px;
}
.roundtable-profile .description {
    height: 8em;
}
.roundtable-profile .actions {
    bottom: 0;
}
.zh-footer {
    font-size: 12px;
}
.modal-dialog-title-text,
.modal-dialog-title-close {
    color: #fff;
}
}
