@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("coliru.stacked-crooked.com") {
  
/*   / *** Frame partitions *** /
  frameset {
    rows: 80%, 100 !important;
  } */
  
  /*** Header ***/
  div.hbox.f0 {
    background-color: #3f3f3f !important;
    color: #aaaaaa !important;
  }
  
  .link.f0.vbox, .link.f1, a[id="link feedbackButton"] {
    color: #aaaaaa;
  }
  
  /*** Editor ***/
  .ace-tm {
    background-color: #282828;
    color: #ccc;
  }
  
  /* 'Run', 'Share', 'Exit' */
  .coliru-btn {
    background-color: #333333;
    background-image: none;
    color: #aaaaaa;
  }
  
  .coliru-btn:hover {
    background-color: #666;
    background-image: none;
    color: #ccc;
  }
  
  /* Compiler Selector */
  .coliru-select-compiler select {
    background-color: #333333;
    background-image: none;
  }
  
  /* blinking cursor */
  .ace-tm .ace_cursor {
    color: #aaaaaa;
  }
  
  /* active line row */
  .ace-tm .ace_gutter-active-line {
    background-color: #a1a1a1;
  }
  
  /* code folds */
  .ace-tm .ace_fold {
    background-color: black;
  }
  
  /* selected word */
  .ace-tm .ace_marker-layer .ace_selection {
    background: rgba(192, 192, 192, 0.15);
  }
  
  .ace-tm .ace_marker-layer .ace_active-line {
    background-color: rgba(0, 0, 0, 0.15);
  }
  
  /* Error symbol */
  .ace_gutter-cell.ace_error {
  }
  
  /* Line No.s */
  .ace-tm .ace_gutter {
    background-color: #333333;
    color: #666;
  }
  
  /* Block Indent vertical line */
  .ace-tm .ace_indent-guide {
    background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAACCAYAAACZgbYnAAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAAOxAAADsQBlSsOGwAAABJJREFUCJlj+P///38GBgaG/wAe6wT8qpLcqwAAAABJRU5ErkJggg==") right repeat-y;
  }
  
  /* C++ color coding */
  
  .ace-tm .ace_storage, .ace-tm .ace_keyword {
    color: #3281c6;
  }
  
  .ace-tm .ace_constant {
    color: #ed2c5d;
  }
  
  .ace-tm .ace_string {
    color: #1f8000;
  }
  
  .ace-tm .ace_constant.ace_numeric {
    color: #ac7339;
  }
  
  /* e.g. printf */
  .ace-tm .ace_support.ace_function {
    color: #66d9ef;
  }
  
  /*** Output ***/
  #output {
    background-color: #333333;
  }
  
  pre.f0 {
    color: #aaaaaa;
  }
  
  /*** bottom Pane on Share ***/
  #footer {
    background-color: #282828;
    color: #ccc;
  }

}
