css:
  /* 

  Bananastreet Dark Theme

  Author: nagrand
  Site: https://bananastreet.ru
  Version: 0.2

  */

  @-moz-document domain(bananastreet.ru) {

    html, body, input, select, textarea, button, label {
        font-family: 'Open Sans', 'PT Sans Caption',  sans-serif;
        color: #e7e8ec;
    }

    body {
        background-color: rgba(0, 0, 0, 0.86);
    }

    h1, h2, h3, h4, h5 {
        color: #dedede;
    }

    a, ins {
        color: #eee;
    }

    a:hover {
        color: rgba(80, 147, 255, 0.95);
        transition: .8s ease;
    }

    #banners-wrapper {
        height: 252px;
        top: 50px;
    }

    .header {
        background: rgba(31, 31, 31, 0.95);
        padding: 0px 100px;
        box-shadow: 0 0 1px #3a579d;
    }

    .header__nav a {
        color: #bfbfbf;
        letter-spacing: 0.05rem;
    }

    .header__nav a:hover {
        color: rgb(14, 151, 255);
        transition: .5s ease;
    }

    .header__search input {
        background: #3c3c3c;
    }

    .button {
        border-radius: 3px;
        color: #fcfcfc;
        background: linear-gradient(-180deg, #4c74a2 0%, #085fa7 100%);
    }

    .button:hover {
        background: linear-gradient(-180deg, #075ea6 0%, #3a579d 100%);
        color: #fff;
        transition: .8s;
    }

    .block {
        background: rgb(25, 25, 25);
        border-radius: 10px;
        margin-bottom: 10px;
    }

    .player {
        background: rgba(23, 23, 23, 0.96);
        box-shadow: 0px 0px 20px -5px #3a579d;
    }

    .player__title {
        font-size: 14px;
        color: #fff;
    }

    .player .rc-slider {
        height: 4px;
    }

    .player .rc-slider .rc-slider-step {
        background: #D9EFFF;
        height: 4px;
        margin-top: 0px;
        margin-left: 2px;
        z-index: 3;
        width: 78px;
        border-radius: 6px;
    }

    .player .rc-slider .rc-slider-track {
        background: #0091ff;
        height: 4px;
        margin-top: 0px;
        z-index: 4;
    }

    .player .player__volume--radio {
        left: 620px;
    }

    .player__button {
        background: none;
    }

    .player__title--radio {
        margin-right: 20px;
        border: none;
        font-size: 11px;
        width: 62px;
    }

    .player__button .icon:hover {
        color: #0091ff;
    }

    .release__title {
        margin-top: 15px;
    }

    .release__title:after {
        background: none;
    }

    .player__time {
        color: #fcfcfc;
    }

    .player .player__like, .player .player__add-to-playlist {
        background: none;
    }

    #position-button.position-button--active .icon {
        background: #0091ff;
        color: #eee;
    }

    .dj--compact .dj__name {
        color: #0091ff;
    }

    .style-checkbox label {
        color: #0091ff;
        box-shadow: 0 0 1px 0 #ffffff, 0 1px 2px 0 rgba(0, 145, 255, 0.69);
        border-radius: 5px;
        height: 28px;
    }

    .style-checkbox label:hover {
        color: #ffffff;
        background-color: rgba(42, 88, 133, 0.42);
    }

    .block h3 {
        line-height: 5px;
        padding: 5px;
        padding-bottom: 15px;
        margin-bottom: 18px;
        border-bottom: 1px rgba(238, 238, 238, 0.09) solid;
    }

    .footer {
        line-height: 23px;
    }

    .release__play, .release__pause, .release__wait {
        background: linear-gradient(-180deg, rgba(255, 0, 0, 0.66) 0%, rgb(0, 126, 255) 100%);
        box-shadow: 0px 1px 10px 1px rgba(0, 145, 255, 0.75);
        transition: .8s ease;
    }

    .releases-grid__release {
        padding: 11px;
    }

    .release__cover {
        opacity: 0.8;
        -moz-opacity: 0.8;
        background-color: #4c74a2;
        border: 2px rgba(36, 36, 36, 0.56) solid;
    }

    .release__cover:hover, .release__cover:active {
        opacity: 1;
        -moz-opacity: 1;
        background-color: #0091ff;
        transition: .4s ease;
    }

    .release__info {
        text-align: center;
    }

    .release__author {
        font-style: italic;
    }

    .block h2 {
        line-height: 24px;
        margin-bottom: 18px;
        text-transform: uppercase;
        font-weight: 300;
        letter-spacing: 0.2rem;
        text-align: center;
    }

    .block__see-all {
        color: rgba(132, 132, 132, 0.89);
        font-style: italic;
    }

    .track__download .icon, .track__playlist-button .icon {
        color: rgba(255, 255, 255, 0.2);
    }

    .track__download:hover .icon, .track__playlist-button:hover .icon {
        color: #0091ff;
    }

    .track__play, .track__pause, .track__wait {
        background: linear-gradient(-180deg, rgba(255, 0, 0, 0.39) 0%, rgba(0, 126, 255, 0.57) 100%);
    }

    .radio-show {
        margin-bottom: 2px;
        padding: 10px;
        background-color: rgba(0, 0, 0, 0.5);
    }

    .radio-show__time {
        margin-right: 15px;
    }

    div .radio-show:first-child {
        background-color: rgba(0, 145, 255, 0.6);
        border-left: 3px #6dd400 solid;
    }

    .circle-button {
        background: linear-gradient(-180deg, rgba(255, 0, 0, 0.34) 0%, rgba(0, 126, 255, 0.37) 100%);
    }

    .circle-button:hover {
        background: linear-gradient(-180deg, rgba(142, 82, 82, 0.26) 0%, #0091ff 100%);
    }

    .tabs:not(.tabs--horizontal) .tabs__tab--active {
        background: #242424;
    }

    .tabs:not(.tabs--horizontal) .tabs__tab:hover {
        background: rgba(0, 145, 255, 0.2);
    }

    .header__search input:valid, .header__search input:focus {
        background: rgb(62, 62, 62);
        color: #fff;
    }

    .quick-search {
        background: #242424;
        border-radius: 10px;
        max-width: 615px;
        top: 47px;
    }

    .quick-search li a {
        color: #eee;
        border-top: 1px solid rgba(238, 238, 238, 0.08);
    }

}
