css:
@-moz-document regexp("https:\/\/www.google.co.in\/(?!.*tbm=isch).*"), regexp("https:\/\/www.google.com\/(?!.*tbm=isch).*") {
Body {
    background: url(https://source.unsplash.com/1920x1080?nature,landscape,mountains) no-repeat center center fixed !important;
    -webkit-background-size: cover !important;
    -moz-background-size: cover !important;
    -o-background-size: cover !important;
    background-size: cover !important;
    overflow-x: hidden !important;
    font-family: Verdana, Geneva, sans-serif;
}
Body a {
    text-decoration: none !important;
    color: #008ada !important;
    transition: color 0.4s ease;
}
Body a:hover {
    text-decoration: none !important;
    color: #00446b !important;
}
#_eEe {
    text-indent: 100% !important;
    white-space: nowrap !important;
    overflow: hidden !important;
}
#gb a.gb_P,
#gb span.gb_P {
    text-shadow: 0px 0px 10px rgba(0, 0, 0, 0) !important;
    color: #ffffff !important;
    font-size: 18px;
    transition: color 0.4s ease;
    z-index: 2;
}
#gb a.gb_P:hover,
#gb span.gb_P:hover {
    color: #F7CA18 !important;
}
#gb {
    background: rgba(0, 0, 0, 0.3) !important;
}
#fbar {
    background: rgba(0, 0, 0, 0.5) !important;
    text-shadow: 0px 0px 10px rgba(0, 0, 0, 0) !important;
    border-top: 0px solid #e4e4e4 !important;
}
#fbar a {
    text-decoration: none !important;
    color: #ffffff !important;
    transition: color 0.4s ease;
}
#fbar a:hover {
    color: #F7CA18 !important;
}
.logo-subtext {
    color: #ffffff !important;
}
.jhp input[type="submit"],
.sbdd_a input,
.gbqfba {
    font-family: Verdana, Geneva, sans-serif;
    background: black;
    color: #FFFFFF;
    text-shadow: none;
    border: 2px solid #F7CA18;
    font-size: 14px;
    border-radius: 0;
    letter-spacing: 2px;
    font-weight: lighter;
    text-align: center;
    text-decoration: none;
    transition: background, color 1s ease;
}
.jhp input[type="submit"]:hover,
.sbdd_a input:hover,
.gbqfba:hover {
    font-family: Verdana, Geneva, sans-serif;
    background: #F7CA18;
    color: #000000 !important;
    border: 2px solid #F7CA18 !important;
}
#rcnt {
    background: white !important;
}
/* 
###########################################
Home Page Style Ends
###########################################
*/

div.col,
div.col *,
div.g {
    box-sizing: border-box;
}
.g {
    background: white;
    padding: 25px;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.1);
    margin-bottom: 15px!important;
    margin-right: 0px;
    transition: box-shadow 0.5s;
    position: relative;
}
.g:hover {
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.1);
}
.sfbg.nojsv .sfbgg {
    background: #fafafa!important;
}
#searchform {
    position: fixed!important;
}
.appbar {
    margin-top: 59px;
    border-bottom: none;
}
#top_nav {
    position: fixed;
    width: 100%;
    z-index: 100;
}
body.vasq #hdtbMenus.hdtb-td-o {
    height: 44px;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.1);
    box-sizing: border-box;
}
#cnt {
    width: 100%;
}
.mw {
    width: 100%;
    max-width: 100%!important;
}
#rcnt {
    width: 100%!important;
    box-sizing: border-box;
    margin-top: 0px;
}
#rcnt .col:nth-of-type(2) {
    width: 65%!important;
    min-width: 582px;
    margin-left: 6%;
}
#rcnt .col:nth-of-type(3) {
    width: 23%!important;
    float: left;
    padding-top: 6px;
}
#rhs .rhsvw {
    width: 100%;
}
.kp-blk {
    box-shadow: none;
}
#center_col {
    width: 100%!important;
    min-width: 0px!important;
    margin-left: 0px!important;
    padding-top: 0px!important;
}
#rhscol {
    width: 20%!important;
    min-width: 200px;
    float: right;
}
#rhs {
    margin-left: 0px!important;
}
.kp-blk._Jw._Rqb._RJe {
    margin: 0px!important;
}
.s {
    max-width: 100%;
}
h3.r:after {
    content: '';
    display: block;
    border-top: 1px solid #ebebeb;
    height: 0px;
    margin-top: 5px;
    margin-bottom: 5px;
}
#center_col .kp-blk {
    margin-left: 0px;
    margin-right: 0px;
}
.kno-ftr {
    margin: 0 -35px 0 -8px;
    padding: 4px 40px 0;
}
.related-question-pair>div>div>div>div {
    padding-top: 10px!important;
}
._U7g._grf {
    padding-top: 10px;
    padding-bottom: 10px;
}
#extrares {
    background: transparent;
    padding: 0px 15px!important;
}
#botstuff {
    background: white;
    padding: 20px;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.1);
    transition: all 0.5s;
    position: relative;
}
#botstuff:hover {
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.1);
}
._Ugf {
    overflow: hidden;
}
a._Eu._H2 {
    display: block;
    width: 100%!important;
    background: white;
    padding: 20px;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.1);
    transition: all 0.5s;
    position: relative;
}
a._Eu._H2:hover {
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.1);
}
div#bcenter {
    display: none;
}
/*remove ads*/

div#tads {
    display: none;
}
div#bottomads {
    display: none;
}
img#hplogo {
    box-sizing: content-box;
}
ol#ab_ctls * {
    box-sizing: content-box!important;
}
g-snapping-carousel {
    margin-left: 12px;
    margin-right: 12px;
}
#foot {
    width: calc(100% - 16px)!important;
    margin-left: 8px;
    box-sizing: border-box;
    padding: 1px;
}
#rhs .kp-blk {
    margin: 0px;
}
div#subform_ctrl {
    margin-top: 0px!important;
}
#slim_appbar {
    height: 44px;
}
#hdtbMenus.hdtb-td-o {
    background: #fafafa;
    top: 59px!important;
}
.appbar {
    margin-top: 40px;
}
#resultStats {
    line-height: 44px!important;
}
#res a:link,
#extrares a:link {
    color: #222!important;
}
#res a:visited,
#extrares a:visited {
    color: #999!important;
}
#appbar {
    background: #fafafa;
}
#res {
    padding: 0px 15px;
}
div._M6k.vk_c {
    width: 100%;
    margin-left: 0px!important;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.1);
}
div._M6k.vk_c:hover {
    box-shadow: 1px 1px 15px rgba(0, 0, 0, 0.1);
}
#hdtbSum {
    background: #fafafa!important;
}
g-card-grid {
    margin: 0px;
}
.hdtb-mn-cont {
    margin-top: 8px;
}
.kno-ftr.rhsvw {
    padding-bottom: 4px;
}
.spch {
    background: #000 !important;
    opacity: 0.9 !important;
}
}
