css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("secureserver.net") {
  body{
    background-color: #101010;
    color:#fff;
  }
  body#cust{
    background-color: #101010;
    color:#fff;
  }
  #cust select, #cust textarea, #cust input[type="text"], #cust input[type="password"], #cust input[type="datetime"], #cust input[type="datetime-local"], #cust input[type="date"], #cust input[type="month"], #cust input[type="time"], #cust input[type="week"], #cust input[type="number"], #cust input[type="email"], #cust input[type="url"], #cust input[type="search"], #cust input[type="tel"], #cust input[type="color"], #i_installatron #i_search_query{
    color: #fff;
  }
  .breadcrumb > .active {
    color: #f3f3f3;
  }
  h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6{
    color:#f3f3f3;
  }
  select, textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="tel"], input[type="color"]{
    color:#fff;
    border: 0px solid;
    background-color: #1c1c1c;
  }
  .cellbox-header{
    border: 0px solid;
    background-color: #1c1c1c;
  }
  .cellbox-header:hover{
    background-color: #111;
  }
  #gl_paper_lantern .widget-list li, .gl_paper_lantern .widget-list li{
    border-bottom: 0px solid;
  }
  .progress{
    background-color: #1c1c1c;
  }
  .modal-header{
    color: #fff;
  }
  .modal-content {
    background-color: #1c1c1c;
  }
  .modal-footer{
    background-color: #111;
  }
  .form-control{
    border: 0px solid;
    color: #fff;
    background-color: #101010;
  }
  .form-control.option-directory-select{
    width: 90%;
  }
  #topnav{
    background-color: #101010;
    border: 0px solid;
  }
  #topnav li a {
    color: #f3f3f3;
  }
  #topnav li a:hover {
    color: grey;
  }
  .input-group-addon{
    background-color: #008c46;
    border: 0px solid;
  }
  .yui-skin-sam .yui-dt-scrollable .yui-dt-hd, .yui-skin-sam .yui-dt-scrollable .yui-dt-bd{
    border-color: transparent;
    background-color: #1c1c1c;
  }
  .yui-skin-sam .yui-dt table{
    border: 0px solid;
  }
  .yui-skin-sam .yui-dt th{
    border-right: none;
  }
  .yui-skin-sam .yui-dt th, .yui-skin-sam .yui-dt th a{
    color: #fff;
  }
  .yui-skin-sam tr.yui-dt-even {
    background-color: #101010;
  }
  .yui-skin-sam tr.yui-dt-odd {
    background-color: #111;
    border: 1px solid #DDDDDD;
  }
  #cust #login-wrapper{
    padding: 80px 0 0 0;
  }
  .spriteicon_img{
    border-radius: 50%;
  }
  #i_cpd .i_cpd_icon a > div > div{
    border-radius: 50%;
  }
  .yuimenu {
    background-color: #111;
  }
  div.yuimenu div.bd {
    background-color: #999;
    border-color: #111;
  }
.table-striped > tbody > tr:nth-child(2n+1) > td, .table-striped > tbody > tr:nth-child(2n+1) > th {
    background-color: #333333;
}
.table thead > tr > th, .table tbody > tr > th, .table tfoot > tr > th, .table thead > tr > td, .table tbody > tr > td, .table tfoot > tr > td {
	border: none;
}
.ssl-table-area .yui-dt tbody.yui-dt-data tr.yui-dt-odd td {
    background-color: #2a2a2e;
}
.ssl-table-area .yui-dt tbody.yui-dt-data tr td.yui-dt-asc, .ssl-table-area .yui-dt tbody.yui-dt-data tr td.yui-dt-desc {
    background-color: #111;
}
.ssl-table-area .yui-dt tbody.yui-dt-data tr.yui-dt-odd td.yui-dt-asc, .ssl-table-area .yui-dt tbody.yui-dt-data tr.yui-dt-odd td.yui-dt-desc {
    background-color: #2a2a2e;
}
.yui-panel .bd, .panel .bd, .yui-panel .ft, .panel .ft {
	border: 2px solid #111 !important;
    border-bottom: 1px solid #111 !important;
    background-color: #111 !important;
}
.button-group button, .button-group button:hover, .button-group button.hover {
    background-color: #111;
    color: #f3f3f3;
    border: 2px solid #111;
    border-top-color: #111;
    border-left-color: #111;
}
  
}
