css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("otvet.mail.ru") {
.x-ph {
line-height:10px!important;
font-size:10px!important;
height:20px!important;
padding:0!important;
}
.x-ph__link, .x-ph__menu__button:link, .x-ph-ff.x-ph-win .x-ph__menu__button{
padding-top:0px!important;
padding-bottom:0px!important;
}
.x-ph .w-x-ph__col {
height:18px;}
.column_left {
background-color:#e0e0e0;
padding:.7em;
}
.page-index--h1{
display:none;
}
.pm-toolbar__search__container {
padding-top:4px!important;
padding-bottom:0px!important;
}
.pm-toolbar__button__inner {
padding:6px 8px 0;
}
.q--li{
padding:.1em 1.25em .1em 3.3em!important;
}
.a--ava {
top:1.0em;
left:0.9em;
border-radius:0%;
}
.a--box {
padding:.8em 0.05em 0.25em 3.9em;
}
.a--atext {
margin-bottom:0;
}
.a--author-container, .a--2line {
display:inline!important;
margin:0!important;
}
.a--2line{
margin-left:9px!important;
}
.column_right, .q-ask{
display:none!important;
}
.fresh {
top:32px!important;}
}
