css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("eldarya.com"), domain("eldarya.es") {

/*Nevra en familier*/  
 
  /*Images - pictures*/
 #pet-headerPortrait-base {
   background: url("https://68.media.tumblr.com/78164ab1b0594c7df7b17896084a6e5f/tumblr_omexekuQxc1sedhvgo2_500.png") -15px top no-repeat !important;
  background-size : auto 125px !important ;
  }

  #pet-portrait {
    background : url("https://68.media.tumblr.com/3a26a954a369aa241a31b13e5ea1664d/tumblr_omexekuQxc1sedhvgo3_500.png") center no-repeat !important ;
    background-size : auto 450px !important ;
    height : 450px !important ;
  }

  #pet-portrait > img[src*="/static/img/pet/icon/"] {display : none !important ;}

    /*Infos*/

  #pet-info-name:before {
    content : "Nevra" !important ;
    font-size : 20px !important ;
  }
  
  #pet-info-name {
    font-size : 0 !important ;
  }  
  
  #pet-info-evolve > .on {font-size : 0 !important ;}
  
  .pet-info-evolve-icon {font-size : 13px !important ;}
  
  .pet-info-evolve-icon:after {
    content : " Nevra está al máximo, ¡no lo hagas esperar más!" ;
  }
  
  
  #pet-info-racegender:before {
    content : "Vampiro" !important ;
    font-size : 16px !important ;
    line-height : 20px !important ;
  } 
  
  #pet-info-racegender {
    font-size : 0 !important ;
    margin-top : 5px ;
  }  
  
  img[src*="/static/img/f.png"] {
    background : url("http://www.eldarya.fr/static/img/m.png") !important ;
    padding-left : 14px !important ;
    padding-top : 15px !important ;
    width : 0 !important ;
    height : 0 !important ; 
    margin-bottom : 5px !important ;
  }
 
 /*Nourriture - food*/
  
 #pet-info-food > img {
  background : url("https://68.media.tumblr.com/cf4e6dba2e22bbe661d89fb115c8a84f/tumblr_omexekuQxc1sedhvgo4_75sq.png") !important ;
  background-size : 60px 60px !important ;
  margin-top : -5px !important ;
  padding-left : 60px !important ;
  padding-top : 60px !important ;
  width : 0 !important ;
  height : 0 !important ;
} 
  
  #pet-info-food {font-size : 0 !important ;}
  
  #pet-info-food:before {
    content : "Sangre" !important ;
    font-size : 16px !important ;
  }
  
  #pet-info-food > span {font-size : 16px !important ;}

  /*Ezarel sur la page du profil*/

  #player-display-pet {
    background : url("https://68.media.tumblr.com/76c8563b566096a188b3c0f8f15de3fc/tumblr_omexekuQxc1sedhvgo1_1280.png") no-repeat ;
    background-size : auto 100% ;
    height : 540px !important ;
    width : 205px !important ;
  }

  #player-display-pet > img {display : none !important ;}
}
