@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

panel#new-tab-overlay {
  border: 1px solid black !important;
  box-shadow: 0px 0px 4px rgba(0,0,0,.5) !important; /*not working*/
}

#new-tab-overlay .panel-arrowcontent {
  margin: 0px !important;
  padding: 0px !important;
}

#new-tab-overlay menuitem.menuitem-iconic {
  background-color: rgb(30, 30, 30) !important;
  height: 24px !important;
  border-radius: 0px !important;
  margin: 0px !important;
  border: 1px solid transparent !important;
  padding: 0px !important;
  box-shadow: none !important;
}

#new-tab-overlay menuitem.menuitem-iconic:hover {
  background-color: rgb(50, 50, 50) !important;
}

.menu-iconic-left {
  height: 50px !important;
}

.menu-iconic-icon {
  height: 16px !important;
}

#new-tab-overlay .menu-iconic-text {
  color: rgb(166, 166, 166) !important;
}

#new-tab-overlay menuitem.menuitem-iconic:hover .menu-iconic-text {
  color: rgb(255, 255, 255) !important;
}
