css:
@-moz-document url-prefix("http://mp3.zing.vn/") {
nav {
    background-color: #dedede;
}
nav .container {
    background-color: #dedede;
}
#sb-site,
body {
    color: #bbb;
    background-image: url(data:image/png;base64,) !important;
}
.des-inside.otr .des {
    position: static;
    background-color: #262626;
    padding: 8px 0 0;
}
.fn-slide-show .section .des-inside .des-more-slim {
    min-height: 40px;
    background-color: #262626;
}
.tab-nav a {
    display: block;
    width: 100%;
    height: 100%;
    background: #dedede;
    color: #333;
    text-align: center;
    font-size: 16px;
    line-height: 42px;
    border-top: 2px solid #ECF0F1;
}
.tab-nav a.active {
    border-top: 2px solid #ea4335;
    border-right: 1px solid #ddd;
    border-left: 1px solid #ddd;
    background: #fff;
}
.wrapper-page {
    background-color: #262626;
    width: 1000px;
    margin: 0 auto;
    padding: 15px 12px 0;
}
.info-top-play .txt-primary,
.info-top-play .txt-primary:hover,
.info-song-top a,
.lyrics-container {
    color: #bbb;
}
#block-lyrics-song {
    background-color: #1c1c1c;
    color: #bbb;
    font-size: 100%;
    line-height: 1.5em;
    background: #1c1c1c;
    border-left: 5px solid #bbb;
    /*border-right: 5px solid #bbb;*/
    
    border-top: 5px solid #bbb;
    /*border-bottom: 5px solid #bbb; */
    
    padding: 2px 6px 5px 6px;
    border-radius: 10px;
}
div#lyrics.lyrics-container.po-r {
    background-color: #1c1c1c;
    color: #bbb;
    font-size: 100%;
    line-height: 1.5em;
}
.lyrics-container .donggop span {
    color: #bbb;
}
.lyrics-container .lyrics-func p a {
    margin-left: 0;
    color: #bbb;
}
.lyrics-container .lyrics-func a {
    margin-left: 25px;
    color: #bbb;
}
.fn-content {
    font-size: 14px;
    border-bottom: 5px solid #bbb;
    border-right: 5px solid #bbb;
    border-radius: 10px;
}
.nine-color,
.item-list .item,
.info-meta,
.box-comment .list-comment .item-comment .post-comment p,
.box-comment .list-comment .item-comment .post-comment .time-comment {
    color: #bbb;
}
.playlist li .item-song {
    background-color: #262626;
    color: #bbb;
    width: 100%;
    line-height: 50px;
    padding: 0 20px;
    float: left;
    border-bottom: 0;
    margin-bottom: 0;
}
.txt-primary {
    color: #bbb;
}
.title-section a {
    color: #ea4335;
}
.title-section.sz-title-sm {
    margin-bottom: 20px;
    color: #ea4335;
}
.section-zingtv {
    background: #262626;
    margin-bottom: 30px;
}
.info-meta {
    color: #bbb;
}
a {
    color: #bbb;
}
/* Block ads  */
.toggle-ads {
    display: none;
}
.partner-box {
    display: none;
}
#_mp3_adsZoneRight1,
#_mp3_adsZoneRight2,
#zone-ads-banner-container > ._bannerAds,
.ads,
.player-bannerads,
#zone-ads-billboard,
#zone-ads-banner-container,
.ads_location {
    display: none;
}
.vast_ad > video {
    display: none;
}
}

@-moz-document url-prefix("http://m.mp3.zing.vn/") {
.info-song .main-info-song .title-main-song {
    display: block;
    margin-bottom: 5px;
    color: #ffffff;
    font-size: 1.125em;
    font-weight: 300;
}
#sb-site,
body {
    color: #fff;
    background-color: #1c1c1c;
    background-image: url(data:image/png;base64,) !important;
}
#box-info > .info-more-text, #box-info {
    background-color: #1c1c1c;
    color: #bbb;
}
.lyric-song .info-more-text p {
    background-color: #1c1c1c;
    color: #bbb;
    font-size: 100%;
    line-height: 1.5em;
    background: #1c1c1c;
    /*border-left: 5px solid #bbb;*/
    
    border-right: 5px solid #bbb;
    /*border-top: 5px solid #bbb; */
    
    border-bottom: 5px solid #bbb;
    padding: 2px 6px 5px 6px;
    border-radius: 10px;
}
.wrap-content .module-hotSongs .module-inside .obj .obj-inside .title-song {
    display: block;
    margin-bottom: 5px;
    color: #bbb;
    font-size: 0.938em;
}
.wrap-content .module-hotSongs .module-inside .obj .num {
    position: absolute;
    z-index: 1;
    top: 50%;
    left: 0;
    width: 23px;
    height: 23px;
    margin-top: -12px;
    line-height: 23px;
    color: #bbb;
    font-size: 0.938em;
    text-align: center;
}
.wrap-content .module-comment .post-comment textarea {
    color: #bbb;
    background-color: #1c1c1c;
    overflow-y: scroll;
    height: auto;
}
.ads_location {
    display: none;
}
}

@-moz-document url-prefix("http://tv.zing"), url-prefix("https://tv.zing"), url-prefix("http://www.zing"), url-prefix("https://www.zing"), url-prefix("http://zing"), url-prefix("https://zing") {
iframe#google_ads_frame1,iframe{
display:none !important;
}
}
