css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.novelupdates.com") {
div.l-main {
    background: rgb(50, 50, 50) !important;
}
/* First Layer Backgrounds ('Rear-most') */

div.l-content, div.nuf_bd, div.widget {
    background: rgb(33, 33, 33) !important;
}
/* Second Layer Backgrounds ('Closest') */

div.wpb_text_column, div.wpb_wrapper, .breadcrumb_nu, .gen_rnd, div.rankfl, div.sf_box, div.rf_more_set {
    background: rgb(25, 25, 25) !important;
}
/* Border Radius (5px) */

div.l-content, div.wpb_text_column, div.wpb_wrapper, div.widget {
    border-radius: 5px !important;
}
/* Title Colours */

div.seriestitlenu, h3.widgettitle, h3.mypage, h4.seriesinfo, h4.seriesother, h5.seriesother, #Breadcrumb_Top dd {
    color: #ccc !important;
}
/* Normal Text */

p, #myrates tr, div.wpb_text_column, div.wpb_wrapper, div.w-comments-item-text, div.sticon, .l-canvas {
    color: #888 !important;
}
/* Remove Top Margin */

div.rl_margin, div.widget.widget_text, h3.widgettitle, div.newfrmposts, div.seriestitlenu, div.l-content.release, h3.mypage.bc.top, div.serieseditimg, div.seticon, .g-hr.type_invisible, div.ricon:not(.sf):not(.bc), .mypage.bc.top {
    margin-top: 0px !important;
}
/* Remove Bottom Margin */

div.digg_pagination, .g-hr.type_invisible, div.l-header, div.seticon, input#submitseries {
    margin-bottom: 0px !important;
}
/* Remove Top Padding */

div.l-sidebar.at_right, .g-html h3, div.seticon {
    padding-top: 0px !important;
}
/* Invert icons */

.seticon img, .item.icon img, .ricon img {
    -webkit-filter: invert(100%) !important;
}
/* Remove borders */

div.nuf_bd *, div.nuf_bd {
    border: none !important;
}
/* Border colours */

table.frmlatest, .bdrank, .breadcrumb_nu, .gen_rnd, .btnrev.review, .report_edit_button, .my_popupreport_close, .my_popup_close, div.rankfl, div.sf_box, div.rf_more_set {
    border-color: rgb(50, 50, 50) !important;
}
/* Remove backgrounds */

.gen_rnd div, .gen_rnd.main tr {
    background: none !important;
}
/* ------------- */
/* Table Entries */
/* ------------- */
/* New entries from your reading lists are the brightest */

tr.newcolorme * {
    background: rgb(50, 50, 50) !important;
}
/* Read entries are slightly darker */

tr.readcolor * {
    background: rgb(40, 40, 40) !important;
}
/* Odd entries are the darkest */

tbody tr:nth-child(2n+1) {
    background: rgb(25, 25, 25) !important;
}
/* Even entries are the same as the normal background colour */

tbody tr:nth-child(2n) {
    background: rgb(33, 33, 33) !important;
}
/* Novel genre colouring to help distinguish them better */

a.genre.odd {
    background: rgb(33, 33, 33) !important;
}
div#select_box_chosen {
    width: 100% !important;
}
div.rssicon {
    margin-top: 2px !important;
}
.g-html ul li {
    padding-bottom: 0px !important;
    margin-bottom: 2px !important;
}
div.l-submain-h, .page-template-pg-seriesfinder div.l-submain-h.g-html.i-cf.other {
    padding: 50px 0px !important;
}
div.digg_pagination {
    background: transparent !important;
}
div.w-blog-content {
    padding-top: 5px !important;
}
div.l-content {
    padding: 0px 10px 10px 10px !important;
    min-height: 0px !important;
}
div.l-footer, div.l-subfooter, th.header {
    background: #2c3e50 !important;
}
thead tr, #myrates tr {
    background: none !important;
}
div.wpb_text_column, div.wpb_wrapper {
    padding: 5px 5px 5px 5px !important;
}
.two-thirds div.wpb_text_column, .two-thirds div.wpb_wrapper {
    /* Needed to make the background cover the .digg_pagination navigation buttons */
    /* Setting to auto causes the '0px' height .digg_pagination::after element to create a scroll bar */
    
    overflow: hidden !important;
}
div#text-9 {
    padding: 0px !important;
    margin: 0px !important;
}
div.widget.widget_text {
    padding: 4px 5px 0.1px 5px !important;
}
h3.widgettitle_nuf {
    padding: 5px !important;
}
div.nuf_bd {
    border-radius: 0px 0px 5px 5px !important;
}
#nuf_li {
    padding-left: 5px !important;
}
.gen_rnd {
    padding: 15px 15px 15px 15px !important;
}
div.seticon {
    top: 0px !important;
}
div.ricon.sf.bc {
    margin-top: -74px !important;
}
div.w-blog-content:last-child br {
    display: none !important;
}
div.w-blog-content, div.rankfl, form#rankfilter, div.sticon {
	padding-bottom: 0px !important;
}
.crfr {
	top: 25px;
}
div[style^="border"] {
	border-color: rgb(50, 50, 50) !important;
    background: rgb(25, 25, 25) !important;
    margin-bottom: 20px !important;
}
}
